   836â†’  font-weight: var(--font-weight-medium);
   837â†’}
   838â†’
   839â†’.connection-indicator--connected {
   840â†’  background: var(--color-success-100);
   841â†’  color: var(--color-success-700);
   842â†’  border: 1px solid var(--color-success-200);
   843â†’}
   844â†’
   845â†’.connection-indicator--connecting {
   846â†’  background: var(--color-warning-100);
   847â†’  color: var(--color-warning-700);
   848â†’  border: 1px solid var(--color-warning-200);
   849â†’}
   850â†’
   851â†’.connection-indicator--disconnected {
   852â†’  background: var(--color-error-100);
   853â†’  color: var(--color-error-700);
   854â†’  border: 1px solid var(--color-error-200);
   855â†’}
   856â†’
   857â†’[data-theme="dark"] .connection-indicator--connected {
   858â†’  background: var(--color-success-950);
   859â†’  color: var(--color-success-300);
   860â†’  border: 1px solid var(--color-success-800);
   861â†’}
   862â†’
   863â†’[data-theme="dark"] .connection-indicator--connecting {
   864â†’  background: var(--color-warning-950);
   865â†’  color: var(--color-warning-300);
   866â†’  border: 1px solid var(--color-warning-800);
   867â†’}
   868â†’
   869â†’[data-theme="dark"] .connection-indicator--disconnected {
   870â†’  background: var(--color-error-950);
   871â†’  color: var(--color-error-300);
   872â†’  border: 1px solid var(--color-error-800);
   873â†’}
   874â†’
   875â†’/* Notification badges */
   876â†’.notification-badge {
   877â†’  display: inline-flex;
   878â†’  align-items: center;
   879â†’  justify-content: center;
   880â†’  min-width: 1.25rem;
   881â†’  height: 1.25rem;
   882â†’  padding: 0 0.25rem;
   883â†’  font-size: 0.75rem;
   884â†’  font-weight: var(--font-weight-bold);
   885â†’  line-height: 1;
   886â†’  border-radius: var(--radius-full);
   887â†’  white-space: nowrap;
   888â†’}
   889â†’
   890â†’.notification-badge--primary {
   891â†’  background: var(--color-primary-500);
   892â†’  color: white;
   893â†’}
   894â†’
   895â†’.notification-badge--error {
   896â†’  background: var(--color-error-500);
   897â†’  color: white;
   898â†’}
   899â†’
   900â†’.notification-badge--warning {
   901â†’  background: var(--color-warning-500);
   902â†’  color: white;
   903â†’}
   904â†’
   905â†’.notification-badge--success {
   906â†’  background: var(--color-success-500);
   907â†’  color: white;
   908â†’}
   909â†’
   910â†’/* Performance optimizations */
   911â†’.will-change-transform {
   912â†’  will-change: transform;
   913â†’}
   914â†’
   915â†’.will-change-opacity {
   916â†’  will-change: opacity;
   917â†’}
   918â†’
   919â†’.gpu-acceleration {
   920â†’  transform: translateZ(0);
   921â†’  backface-visibility: hidden;
   922â†’  perspective: 1000;
   923â†’}
   924â†’
   925â†’/* Battery optimization styles */
   926â†’@media (max-width: 768px) {
   927â†’  .battery-optimize {
   928â†’    animation-duration: 0s !important;
   929â†’    transition-duration: 0.1s !important;
   930â†’  }
   931â†’  
   932â†’  .battery-optimize .glass-medium,
   933â†’  .battery-optimize .glass-strong {
   934â†’    backdrop-filter: none !important;
   935â†’    -webkit-backdrop-filter: none !important;
   936â†’    background: var(--color-surface-primary) !important;
   937â†’  }
   938â†’}
   939â†’
   940â†’/* Print styles */
   941â†’@media print {
   942â†’  .no-print {
   943â†’    display: none !important;
   944â†’  }
   945â†’  
   946â†’  .notifications-container,
   947â†’  .chat-widget-container,
   948â†’  .connection-indicator {
   949â†’    display: none !important;
   950â†’  }
   951â†’  
   952â†’  * {
   953â†’    background: transparent !important;
   954â†’    color: black !important;
   955â†’    box-shadow: none !important;
   956â†’    text-shadow: none !important;
   957â†’  }
   958â†’}
   959â†’
   960â†’/* ===== MOBILE OPTIMIZATIONS ===== */
   961â†’
   962â†’/* Touch optimization styles */
   963â†’.touch-optimized {
   964â†’  -webkit-tap-highlight-color: transparent;
   965â†’  -webkit-touch-callout: none;
   966â†’  -webkit-user-select: none;
   967â†’  user-select: none;
   968â†’}
   969â†’
   970â†’.touch-optimized button,
   971â†’.touch-optimized [role="button"],
   972â†’.touch-optimized a,
   973â†’.touch-optimized input,
   974â†’.touch-optimized select,
   975â†’.touch-optimized textarea {
   976â†’  -webkit-user-select: text;
   977â†’  user-select: text;
   978â†’}
   979â†’
   980â†’/* Large touch targets for mobile */
   981â†’.large-touch-targets button,
   982â†’.large-touch-targets [role="button"],
   983â†’.large-touch-targets a,
   984â†’.large-touch-targets .btn-base {
   985â†’  min-height: 44px;
   986â†’  min-width: 44px;
   987â†’  padding: var(--spacing-3) var(--spacing-4);
   988â†’}
   989â†’
   990â†’.large-touch-targets .btn-sm {
   991â†’  min-height: 40px;
   992â†’  min-width: 40px;
   993â†’}
   994â†’
   995â†’.large-touch-targets .btn-lg {
   996â†’  min-height: 48px;
   997â†’  min-width: 48px;
   998â†’}
   999â†’
  1000â†’/* Performance mode optimizations */
  1001â†’.performance-mode * {
  1002â†’  will-change: auto !important;
  1003â†’  animation-duration: 0.1s !important;
  1004â†’  transition-duration: 0.1s !important;
  1005â†’}
  1006â†’
  1007â†’.performance-mode .glass-medium,
  1008â†’.performance-mode .glass-strong {
  1009â†’  backdrop-filter: none !important;
  1010â†’  -webkit-backdrop-filter: none !important;
  1011â†’  background: var(--color-surface-primary) !important;
  1012â†’}
  1013â†’
  1014â†’/* Reduced animations for accessibility and battery */
  1015â†’.reduce-animations * {
  1016â†’  animation-duration: 0.01ms !important;
  1017â†’  animation-iteration-count: 1 !important;
  1018â†’  transition-duration: 0.01ms !important;
  1019â†’}
  1020â†’
  1021â†’.reduce-animations .animate-pulse,
  1022â†’.reduce-animations .animate-bounce,
  1023â†’.reduce-animations .animate-spin {
  1024â†’  animation: none !important;
  1025â†’}
  1026â†’
  1027â†’/* Mobile-specific breakpoints */
  1028â†’@media (max-width: 480px) {
  1029â†’  /* Extra small mobile phones */
  1030â†’  .responsive-mobile-xs {
  1031â†’    font-size: 14px;
  1032â†’    padding: var(--spacing-2);
  1033â†’  }
  1034â†’  
  1035â†’  .btn-base {
  1036â†’    font-size: var(--font-size-sm);
  1037â†’    padding: var(--spacing-2) var(--spacing-3);
  1038â†’  }
  1039â†’  
  1040â†’  .modal-backdrop {
  1041â†’    backdrop-filter: none;
  1042â†’    -webkit-backdrop-filter: none;
  1043â†’  }
  1044â†’}
  1045â†’
  1046â†’@media (max-width: 640px) {
  1047â†’  /* Small mobile devices */
  1048â†’  .responsive-mobile-sm {
  1049â†’    padding: var(--spacing-3);
  1050â†’  }
  1051â†’  
  1052â†’  .card-base {
  1053â†’    border-radius: var(--radius-lg);
  1054â†’    margin: var(--spacing-2);
  1055â†’  }
  1056â†’  
  1057â†’  .glass-medium,
  1058â†’  .glass-strong {
  1059â†’    backdrop-filter: blur(8px) !important;
  1060â†’    -webkit-backdrop-filter: blur(8px) !important;
  1061â†’  }
  1062â†’}
  1063â†’
  1064â†’/* Landscape mobile orientation */
  1065â†’@media (max-height: 500px) and (orientation: landscape) {
  1066â†’  .landscape-optimize {
  1067â†’    height: 100vh;
  1068â†’    overflow-y: auto;
  1069â†’  }
  1070â†’  
  1071â†’  .notifications-container {
  1072â†’    top: var(--spacing-2);
  1073â†’    right: var(--spacing-2);
  1074â†’  }
  1075â†’  
  1076â†’  .chat-widget-container {
  1077â†’    bottom: var(--spacing-2);
  1078â†’    right: var(--spacing-2);
  1079â†’  }
  1080â†’}
  1081â†’
  1082â†’/* High DPI displays (retina) */
  1083â†’@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1084â†’  .retina-optimize {
  1085â†’    image-rendering: -webkit-optimize-contrast;
  1086â†’    image-rendering: crisp-edges;
  1087â†’  }
  1088â†’  
  1089â†’  .retina-optimize img {
  1090â†’    image-rendering: -webkit-optimize-contrast;
  1091â†’    image-rendering: crisp-edges;
  1092â†’  }
  1093â†’}
  1094â†’
  1095â†’/* Hover detection for touch devices */
  1096â†’@media (hover: none) {
  1097â†’  .hover-only {
  1098â†’    display: none !important;
  1099â†’  }
  1100â†’  
  1101â†’  .btn-base:hover {
  1102â†’    transform: none !important;
  1103â†’  }
  1104â†’  
  1105â†’  .card-base:hover {
  1106â†’    transform: none !important;
  1107â†’    box-shadow: var(--shadow-sm) !important;
  1108â†’  }
  1109â†’}
  1110â†’
  1111â†’/* Pointer device optimization */
  1112â†’@media (pointer: coarse) {
  1113â†’  /* Touch devices */
  1114â†’  .form-control-base {
  1115â†’    font-size: 16px; /* Prevents zoom on iOS */
  1116â†’    padding: var(--spacing-3);
  1117â†’  }
  1118â†’  
  1119â†’  .btn-base {
  1120â†’    padding: var(--spacing-3) var(--spacing-4);
  1121â†’  }
  1122â†’  
  1123â†’  select,
  1124â†’  input,
  1125â†’  textarea {
  1126â†’    font-size: 16px; /* Prevents zoom on iOS */
  1127â†’  }
  1128â†’}
  1129â†’
  1130â†’/* Safe area insets for devices with notches */
  1131â†’@supports (padding: max(0px)) {
  1132â†’  .safe-area-inset-top {
  1133â†’    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1134â†’  }
  1135â†’  
  1136â†’  .safe-area-inset-bottom {
  1137â†’    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1138â†’  }
  1139â†’  
  1140â†’  .safe-area-inset-left {
  1141â†’    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1142â†’  }
  1143â†’  
  1144â†’  .safe-area-inset-right {
  1145â†’    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1146â†’  }
  1147â†’}
  1148â†’
  1149â†’/* PWA display modes */
  1150â†’@media (display-mode: standalone) {
  1151â†’  .pwa-standalone {
  1152â†’    padding-top: var(--spacing-4);
  1153â†’  }
  1154â†’  
  1155â†’  .pwa-standalone .navbar {
  1156â†’    padding-top: env(safe-area-inset-top);
  1157â†’  }
  1158â†’}
  1159â†’
  1160â†’/* Connection-aware styles */
  1161â†’.slow-connection .glass-medium,
  1162â†’.slow-connection .glass-strong {
  1163â†’  backdrop-filter: none !important;
  1164â†’  -webkit-backdrop-filter: none !important;
  1165â†’  background: var(--color-surface-primary) !important;
  1166â†’}
  1167â†’
  1168â†’.slow-connection img {
  1169â†’  filter: blur(2px);
  1170â†’  transition: filter var(--transition-base);
  1171â†’}
  1172â†’
  1173â†’.slow-connection img.loaded {
  1174â†’  filter: none;
  1175â†’}
  1176â†’
  1177â†’/* Gesture feedback */
  1178â†’.gesture-feedback {
  1179â†’  position: relative;
  1180â†’  overflow: hidden;
  1181â†’}
  1182â†’
  1183â†’.gesture-feedback::after {
  1184â†’  content: '';
  1185â†’  position: absolute;
  1186â†’  top: 50%;
  1187â†’  left: 50%;
  1188â†’  width: 0;
  1189â†’  height: 0;
  1190â†’  border-radius: 50%;
  1191â†’  background: rgba(0, 0, 0, 0.1);
  1192â†’  transform: translate(-50%, -50%);
  1193â†’  transition: width 0.3s ease, height 0.3s ease;
  1194â†’  pointer-events: none;
  1195â†’}
  1196â†’
  1197â†’.gesture-feedback.active::after {
  1198â†’  width: 100px;
  1199â†’  height: 100px;
  1200â†’}
  1201â†’
  1202â†’/* Dark mode gesture feedback */
  1203â†’[data-theme="dark"] .gesture-feedback::after {
  1204â†’  background: rgba(255, 255, 255, 0.1);
  1205â†’}
  1206â†’
  1207â†’/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1208â†’
  1209â†’/* Swipe gesture indicators */
  1210â†’.swipe-indicator {
  1211â†’  position: relative;
  1212â†’}
  1213â†’
  1214â†’.swipe-indicator::before {
  1215â†’  content: '';
  1216â†’  position: absolute;
  1217â†’  top: 50%;
  1218â†’  right: var(--spacing-4);
  1219â†’  width: 20px;
  1220â†’  height: 2px;
  1221â†’  background: var(--color-gray-400);
  1222â†’  transform: translateY(-50%);
  1223â†’  opacity: 0.5;
  1224â†’}
  1225â†’
  1226â†’.swipe-indicator::after {
  1227â†’  content: '';
  1228â†’  position: absolute;
  1229â†’  top: 50%;
  1230â†’  right: var(--spacing-3);
  1231â†’  width: 0;
  1232â†’  height: 0;
  1233â†’  border-left: 6px solid var(--color-gray-400);
  1234â†’  border-top: 4px solid transparent;
  1235â†’  border-bottom: 4px solid transparent;
  1236â†’  transform: translateY(-50%);
  1237â†’  opacity: 0.5;
  1238â†’}
  1239â†’
  1240â†’/* Loading states optimized for mobile */
  1241â†’.mobile-loading {
  1242â†’  display: flex;
  1243â†’  flex-direction: column;
  1244â†’  align-items: center;
  1245â†’  justify-content: center;
  1246â†’  min-height: 200px;
  1247â†’  padding: var(--spacing-8);
  1248â†’}
  1249â†’
  1250â†’.mobile-loading .loading-spinner {
  1251â†’  width: 40px;
  1252â†’  height: 40px;
  1253â†’  border: 3px solid var(--color-gray-200);
  1254â†’  border-top: 3px solid var(--color-primary-500);
  1255â†’  border-radius: 50%;
  1256â†’  animation: spin 1s linear infinite;
  1257â†’}
  1258â†’
  1259â†’.mobile-loading .loading-text {
  1260â†’  margin-top: var(--spacing-4);
  1261â†’  font-size: var(--font-size-sm);
  1262â†’  color: var(--color-text-secondary);
  1263â†’  text-align: center;
  1264â†’}
  1265â†’
  1266â†’/* Battery optimization styles */
  1267â†’.battery-optimize {
  1268â†’  animation-play-state: paused !important;
  1269â†’}
  1270â†’
  1271â†’.battery-optimize * {
  1272â†’  animation-play-state: paused !important;
  1273â†’  transition-duration: 0.1s !important;
  1274â†’}
  1275â†’
  1276â†’.battery-optimize .glass-medium,
  1277â†’.battery-optimize .glass-strong {
  1278â†’  backdrop-filter: none !important;
  1279â†’  -webkit-backdrop-filter: none !important;
  1280â†’  background: var(--color-surface-primary) !important;
  1281â†’}
  1282â†’
  1283â†’.battery-optimize .animate-pulse,
  1284â†’.battery-optimize .animate-bounce,
  1285â†’.battery-optimize .animate-spin {
  1286â†’  animation: none !important;
  1287â†’}
  1288â†’
  1289â†’/* Accessibility enhancements for mobile */
  1290â†’@media (max-width: 768px) {
  1291â†’  .mobile-a11y button,
  1292â†’  .mobile-a11y [role="button"],
  1293â†’  .mobile-a11y input,
  1294â†’  .mobile-a11y select {
  1295â†’    min-height: 44px;
