  1169â†’}
  1170â†’
  1171â†’/* Safe area insets for devices with notches */
  1172â†’@supports (padding: max(0px)) {
  1173â†’  .safe-area-inset-top {
  1174â†’    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1175â†’  }
  1176â†’  
  1177â†’  .safe-area-inset-bottom {
  1178â†’    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1179â†’  }
  1180â†’  
  1181â†’  .safe-area-inset-left {
  1182â†’    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1183â†’  }
  1184â†’  
  1185â†’  .safe-area-inset-right {
  1186â†’    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1187â†’  }
  1188â†’}
  1189â†’
  1190â†’/* PWA display modes */
  1191â†’@media (display-mode: standalone) {
  1192â†’  .pwa-standalone {
  1193â†’    padding-top: var(--spacing-4);
  1194â†’  }
  1195â†’  
  1196â†’  .pwa-standalone .navbar {
  1197â†’    padding-top: env(safe-area-inset-top);
  1198â†’  }
  1199â†’}
  1200â†’
  1201â†’/* Connection-aware styles */
  1202â†’.slow-connection .glass-medium,
  1203â†’.slow-connection .glass-strong {
  1204â†’  backdrop-filter: none !important;
  1205â†’  -webkit-backdrop-filter: none !important;
  1206â†’  background: var(--color-surface-primary) !important;
  1207â†’}
  1208â†’
  1209â†’.slow-connection img {
  1210â†’  filter: blur(2px);
  1211â†’  transition: filter var(--transition-base);
  1212â†’}
  1213â†’
  1214â†’.slow-connection img.loaded {
  1215â†’  filter: none;
  1216â†’}
  1217â†’
  1218â†’/* Gesture feedback */
  1219â†’.gesture-feedback {
  1220â†’  position: relative;
  1221â†’  overflow: hidden;
  1222â†’}
  1223â†’
  1224â†’.gesture-feedback::after {
  1225â†’  content: '';
  1226â†’  position: absolute;
  1227â†’  top: 50%;
  1228â†’  left: 50%;
  1229â†’  width: 0;
  1230â†’  height: 0;
  1231â†’  border-radius: 50%;
  1232â†’  background: rgba(0, 0, 0, 0.1);
  1233â†’  transform: translate(-50%, -50%);
  1234â†’  transition: width 0.3s ease, height 0.3s ease;
  1235â†’  pointer-events: none;
  1236â†’}
  1237â†’
  1238â†’.gesture-feedback.active::after {
  1239â†’  width: 100px;
  1240â†’  height: 100px;
  1241â†’}
  1242â†’
  1243â†’/* Dark mode gesture feedback */
  1244â†’[data-theme="dark"] .gesture-feedback::after {
  1245â†’  background: rgba(255, 255, 255, 0.1);
  1246â†’}
  1247â†’
  1248â†’/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1249â†’
  1250â†’/* Swipe gesture indicators */
  1251â†’.swipe-indicator {
  1252â†’  position: relative;
  1253â†’}
  1254â†’
  1255â†’.swipe-indicator::before {
  1256â†’  content: '';
  1257â†’  position: absolute;
  1258â†’  top: 50%;
  1259â†’  right: var(--spacing-4);
  1260â†’  width: 20px;
  1261â†’  height: 2px;
  1262â†’  background: var(--color-gray-400);
  1263â†’  transform: translateY(-50%);
  1264â†’  opacity: 0.5;
  1265â†’}
  1266â†’
  1267â†’.swipe-indicator::after {
  1268â†’  content: '';
  1269â†’  position: absolute;
  1270â†’  top: 50%;
  1271â†’  right: var(--spacing-3);
  1272â†’  width: 0;
  1273â†’  height: 0;
  1274â†’  border-left: 6px solid var(--color-gray-400);
  1275â†’  border-top: 4px solid transparent;
  1276â†’  border-bottom: 4px solid transparent;
  1277â†’  transform: translateY(-50%);
  1278â†’  opacity: 0.5;
  1279â†’}
  1280â†’
  1281â†’/* Loading states optimized for mobile */
  1282â†’.mobile-loading {
  1283â†’  display: flex;
  1284â†’  flex-direction: column;
  1285â†’  align-items: center;
  1286â†’  justify-content: center;
  1287â†’  min-height: 200px;
  1288â†’  padding: var(--spacing-8);
  1289â†’}
  1290â†’
  1291â†’.mobile-loading .loading-spinner {
  1292â†’  width: 40px;
  1293â†’  height: 40px;
  1294â†’  border: 3px solid var(--color-gray-200);
  1295â†’  border-top: 3px solid var(--color-primary-500);
  1296â†’  border-radius: 50%;
  1297â†’  animation: spin 1s linear infinite;
  1298â†’}
  1299â†’
  1300â†’.mobile-loading .loading-text {
  1301â†’  margin-top: var(--spacing-4);
  1302â†’  font-size: var(--font-size-sm);
  1303â†’  color: var(--color-text-secondary);
  1304â†’  text-align: center;
  1305â†’}
  1306â†’
  1307â†’/* Battery optimization styles */
  1308â†’.battery-optimize {
  1309â†’  animation-play-state: paused !important;
  1310â†’}
  1311â†’
  1312â†’.battery-optimize * {
  1313â†’  animation-play-state: paused !important;
  1314â†’  transition-duration: 0.1s !important;
  1315â†’}
  1316â†’
  1317â†’.battery-optimize .glass-medium,
  1318â†’.battery-optimize .glass-strong {
  1319â†’  backdrop-filter: none !important;
  1320â†’  -webkit-backdrop-filter: none !important;
  1321â†’  background: var(--color-surface-primary) !important;
  1322â†’}
  1323â†’
  1324â†’.battery-optimize .animate-pulse,
  1325â†’.battery-optimize .animate-bounce,
  1326â†’.battery-optimize .animate-spin {
  1327â†’  animation: none !important;
  1328â†’}
  1329â†’
  1330â†’/* Accessibility enhancements for mobile */
  1331â†’@media (max-width: 768px) {
  1332â†’  .mobile-a11y button,
  1333â†’  .mobile-a11y [role="button"],
  1334â†’  .mobile-a11y input,
  1335â†’  .mobile-a11y select {
  1336â†’    min-height: 44px;
  1337â†’    font-size: 16px;
  1338â†’  }
  1339â†’  
  1340â†’  .mobile-a11y .focus-ring:focus-visible {
  1341â†’    outline: 3px solid var(--color-primary-500);
  1342â†’    outline-offset: 2px;
  1343â†’  }
  1344â†’}

