   435â†’    opacity: 1;
   436â†’  }
   437â†’  to {
   438â†’    opacity: 0;
   439â†’  }
   440â†’}
   441â†’
   442â†’@keyframes slideInUp {
   443â†’  from {
   444â†’    opacity: 0;
   445â†’    transform: translateY(1rem);
   446â†’  }
   447â†’  to {
   448â†’    opacity: 1;
   449â†’    transform: translateY(0);
   450â†’  }
   451â†’}
   452â†’
