   455â†’    opacity: 0;
   456â†’    transform: translateY(-1rem);
   457â†’  }
   458â†’  to {
   459â†’    opacity: 1;
   460â†’    transform: translateY(0);
   461â†’  }
   462â†’}
   463â†’
   464â†’@keyframes scaleIn {
   465â†’  from {
   466â†’    opacity: 0;
   467â†’    transform: scale(0.9);
   468â†’  }
   469â†’  to {
   470â†’    opacity: 1;
   471â†’    transform: scale(1);
   472â†’  }
   473â†’}
   474â†’
   475â†’@keyframes scaleOut {
   476â†’  from {
   477â†’    opacity: 1;
   478â†’    transform: scale(1);
   479â†’  }
   480â†’  to {
   481â†’    opacity: 0;
   482â†’    transform: scale(0.9);
   483â†’  }
   484â†’}
   485â†’
   486â†’@keyframes pulse {
   487â†’  0%, 100% {
   488â†’    opacity: 1;
