   937â†’  color: var(--color-error-300);
   938â†’  border: 1px solid var(--color-error-800);
   939â†’}
   940â†’
   941â†’/* Notification badges */
   942â†’.notification-badge {
   943â†’  display: inline-flex;
   944â†’  align-items: center;
   945â†’  justify-content: center;
   946â†’  min-width: 1.25rem;
   947â†’  height: 1.25rem;
   948â†’  padding: 0 0.25rem;
   949â†’  font-size: 0.75rem;
   950â†’  font-weight: var(--font-weight-bold);
   951â†’  line-height: 1;
   952â†’  border-radius: var(--radius-full);
   953â†’  white-space: nowrap;
   954â†’}
   955â†’
   956â†’.notification-badge--primary {
   957â†’  background: var(--color-primary-500);
   958â†’  color: white;
   959â†’}
   960â†’
   961â†’.notification-badge--error {
   962â†’  background: var(--color-error-500);
   963â†’  color: white;
   964â†’}
   965â†’
   966â†’.notification-badge--warning {
   967â†’  background: var(--color-warning-500);
   968â†’  color: white;
   969â†’}
   970â†’
   971â†’.notification-badge--success {
   972â†’  background: var(--color-success-500);
   973â†’  color: white;
   974â†’}
   975â†’
   976â†’/* Performance optimizations */
   977â†’.will-change-transform {
   978â†’  will-change: transform;
   979â†’}
   980â†’
   981â†’.will-change-opacity {
   982â†’  will-change: opacity;
   983â†’}
   984â†’
   985â†’.gpu-acceleration {
   986â†’  transform: translateZ(0);
   987â†’  backface-visibility: hidden;
   988â†’  perspective: 1000;
   989â†’}
   990â†’
   991â†’/* Battery optimization styles */
   992â†’@media (max-width: 768px) {
   993â†’  .battery-optimize {
   994â†’    animation-duration: 0s !important;
   995â†’    transition-duration: 0.1s !important;
   996â†’  }
   997â†’  
   998â†’  .battery-optimize .glass-medium,
   999â†’  .battery-optimize .glass-strong {
  1000â†’    backdrop-filter: none !important;
  1001â†’    -webkit-backdrop-filter: none !important;
  1002â†’    background: var(--color-surface-primary) !important;
  1003â†’  }
  1004â†’}
  1005â†’
  1006â†’/* Print styles */
  1007â†’@media print {
  1008â†’  .no-print {
  1009â†’    display: none !important;
  1010â†’  }
  1011â†’  
  1012â†’  .notifications-container,
  1013â†’  .chat-widget-container,
  1014â†’  .connection-indicator {
  1015â†’    display: none !important;
  1016â†’  }
  1017â†’  
  1018â†’  * {
  1019â†’    background: transparent !important;
  1020â†’    color: black !important;
  1021â†’    box-shadow: none !important;
  1022â†’    text-shadow: none !important;
  1023â†’  }
  1024â†’}
  1025â†’
  1026â†’/* ===== MOBILE OPTIMIZATIONS ===== */
  1027â†’
  1028â†’/* Touch optimization styles */
  1029â†’.touch-optimized {
  1030â†’  -webkit-tap-highlight-color: transparent;
  1031â†’  -webkit-touch-callout: none;
  1032â†’  -webkit-user-select: none;
  1033â†’  user-select: none;
  1034â†’}
  1035â†’
  1036â†’.touch-optimized button,
  1037â†’.touch-optimized [role="button"],
  1038â†’.touch-optimized a,
  1039â†’.touch-optimized input,
  1040â†’.touch-optimized select,
  1041â†’.touch-optimized textarea {
  1042â†’  -webkit-user-select: text;
  1043â†’  user-select: text;
  1044â†’}
  1045â†’
  1046â†’/* Large touch targets for mobile */
  1047â†’.large-touch-targets button,
  1048â†’.large-touch-targets [role="button"],
  1049â†’.large-touch-targets a,
  1050â†’.large-touch-targets .btn-base {
  1051â†’  min-height: 44px;
  1052â†’  min-width: 44px;
  1053â†’  padding: var(--spacing-3) var(--spacing-4);
  1054â†’}
  1055â†’
  1056â†’.large-touch-targets .btn-sm {
  1057â†’  min-height: 40px;
  1058â†’  min-width: 40px;
  1059â†’}
  1060â†’
  1061â†’.large-touch-targets .btn-lg {
  1062â†’  min-height: 48px;
  1063â†’  min-width: 48px;
  1064â†’}
  1065â†’
  1066â†’/* Performance mode optimizations */
  1067â†’.performance-mode * {
  1068â†’  will-change: auto !important;
  1069â†’  animation-duration: 0.1s !important;
  1070â†’  transition-duration: 0.1s !important;
  1071â†’}
  1072â†’
  1073â†’.performance-mode .glass-medium,
  1074â†’.performance-mode .glass-strong {
  1075â†’  backdrop-filter: none !important;
  1076â†’  -webkit-backdrop-filter: none !important;
  1077â†’  background: var(--color-surface-primary) !important;
  1078â†’}
  1079â†’
  1080â†’/* Reduced animations for accessibility and battery */
  1081â†’.reduce-animations * {
  1082â†’  animation-duration: 0.01ms !important;
  1083â†’  animation-iteration-count: 1 !important;
  1084â†’  transition-duration: 0.01ms !important;
  1085â†’}
  1086â†’
  1087â†’.reduce-animations .animate-pulse,
  1088â†’.reduce-animations .animate-bounce,
  1089â†’.reduce-animations .animate-spin {
  1090â†’  animation: none !important;
  1091â†’}
  1092â†’
  1093â†’/* Mobile-specific breakpoints */
  1094â†’@media (max-width: 480px) {
  1095â†’  /* Extra small mobile phones */
  1096â†’  .responsive-mobile-xs {
  1097â†’    font-size: 14px;
  1098â†’    padding: var(--spacing-2);
  1099â†’  }
  1100â†’  
  1101â†’  .btn-base {
  1102â†’    font-size: var(--font-size-sm);
  1103â†’    padding: var(--spacing-2) var(--spacing-3);
  1104â†’  }
  1105â†’  
  1106â†’  .modal-backdrop {
  1107â†’    backdrop-filter: none;
  1108â†’    -webkit-backdrop-filter: none;
  1109â†’  }
  1110â†’}
  1111â†’
  1112â†’@media (max-width: 640px) {
  1113â†’  /* Small mobile devices */
  1114â†’  .responsive-mobile-sm {
  1115â†’    padding: var(--spacing-3);
  1116â†’  }
  1117â†’  
  1118â†’  .card-base {
  1119â†’    border-radius: var(--radius-lg);
  1120â†’    margin: var(--spacing-2);
  1121â†’  }
  1122â†’  
  1123â†’  .glass-medium,
  1124â†’  .glass-strong {
  1125â†’    backdrop-filter: blur(8px) !important;
  1126â†’    -webkit-backdrop-filter: blur(8px) !important;
  1127â†’  }
  1128â†’}
  1129â†’
  1130â†’/* Landscape mobile orientation */
  1131â†’@media (max-height: 500px) and (orientation: landscape) {
  1132â†’  .landscape-optimize {
  1133â†’    height: 100vh;
  1134â†’    overflow-y: auto;
  1135â†’  }
  1136â†’  
  1137â†’  .notifications-container {
  1138â†’    top: var(--spacing-2);
  1139â†’    right: var(--spacing-2);
  1140â†’  }
  1141â†’  
  1142â†’  .chat-widget-container {
  1143â†’    bottom: var(--spacing-2);
  1144â†’    right: var(--spacing-2);
  1145â†’  }
  1146â†’}
  1147â†’
  1148â†’/* High DPI displays (retina) */
  1149â†’@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1150â†’  .retina-optimize {
  1151â†’    image-rendering: -webkit-optimize-contrast;
  1152â†’    image-rendering: crisp-edges;
  1153â†’  }
  1154â†’  
  1155â†’  .retina-optimize img {
  1156â†’    image-rendering: -webkit-optimize-contrast;
  1157â†’    image-rendering: crisp-edges;
  1158â†’  }
  1159â†’}
  1160â†’
  1161â†’/* Hover detection for touch devices */
  1162â†’@media (hover: none) {
  1163â†’  .hover-only {
  1164â†’    display: none !important;
  1165â†’  }
  1166â†’  
  1167â†’  .btn-base:hover {
  1168â†’    transform: none !important;
  1169â†’  }
  1170â†’  
  1171â†’  .card-base:hover {
  1172â†’    transform: none !important;
  1173â†’    box-shadow: var(--shadow-sm) !important;
  1174â†’  }
  1175â†’}
  1176â†’
  1177â†’/* Pointer device optimization */
  1178â†’@media (pointer: coarse) {
  1179â†’  /* Touch devices */
  1180â†’  .form-control-base {
  1181â†’    font-size: 16px; /* Prevents zoom on iOS */
  1182â†’    padding: var(--spacing-3);
  1183â†’  }
  1184â†’  
  1185â†’  .btn-base {
  1186â†’    padding: var(--spacing-3) var(--spacing-4);
  1187â†’  }
  1188â†’  
  1189â†’  select,
  1190â†’  input,
  1191â†’  textarea {
  1192â†’    font-size: 16px; /* Prevents zoom on iOS */
  1193â†’  }
  1194â†’}
  1195â†’
  1196â†’/* Safe area insets for devices with notches */
  1197â†’@supports (padding: max(0px)) {
  1198â†’  .safe-area-inset-top {
  1199â†’    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1200â†’  }
  1201â†’  
  1202â†’  .safe-area-inset-bottom {
  1203â†’    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1204â†’  }
  1205â†’  
  1206â†’  .safe-area-inset-left {
  1207â†’    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1208â†’  }
  1209â†’  
  1210â†’  .safe-area-inset-right {
  1211â†’    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1212â†’  }
  1213â†’}
  1214â†’
  1215â†’/* PWA display modes */
  1216â†’@media (display-mode: standalone) {
  1217â†’  .pwa-standalone {
  1218â†’    padding-top: var(--spacing-4);
  1219â†’  }
  1220â†’  
  1221â†’  .pwa-standalone .navbar {
  1222â†’    padding-top: env(safe-area-inset-top);
  1223â†’  }
  1224â†’}
  1225â†’
  1226â†’/* Connection-aware styles */
  1227â†’.slow-connection .glass-medium,
  1228â†’.slow-connection .glass-strong {
  1229â†’  backdrop-filter: none !important;
  1230â†’  -webkit-backdrop-filter: none !important;
  1231â†’  background: var(--color-surface-primary) !important;
  1232â†’}
  1233â†’
  1234â†’.slow-connection img {
  1235â†’  filter: blur(2px);
  1236â†’  transition: filter var(--transition-base);
  1237â†’}
  1238â†’
  1239â†’.slow-connection img.loaded {
  1240â†’  filter: none;
  1241â†’}
  1242â†’
  1243â†’/* Gesture feedback */
  1244â†’.gesture-feedback {
  1245â†’  position: relative;
  1246â†’  overflow: hidden;
  1247â†’}
  1248â†’
  1249â†’.gesture-feedback::after {
  1250â†’  content: '';
  1251â†’  position: absolute;
  1252â†’  top: 50%;
  1253â†’  left: 50%;
  1254â†’  width: 0;
  1255â†’  height: 0;
  1256â†’  border-radius: 50%;
  1257â†’  background: rgba(0, 0, 0, 0.1);
  1258â†’  transform: translate(-50%, -50%);
  1259â†’  transition: width 0.3s ease, height 0.3s ease;
  1260â†’  pointer-events: none;
  1261â†’}
  1262â†’
  1263â†’.gesture-feedback.active::after {
  1264â†’  width: 100px;
  1265â†’  height: 100px;
  1266â†’}
  1267â†’
  1268â†’/* Dark mode gesture feedback */
  1269â†’[data-theme="dark"] .gesture-feedback::after {
  1270â†’  background: rgba(255, 255, 255, 0.1);
  1271â†’}
  1272â†’
  1273â†’/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1274â†’
  1275â†’/* Swipe gesture indicators */
  1276â†’.swipe-indicator {
  1277â†’  position: relative;
  1278â†’}
  1279â†’
  1280â†’.swipe-indicator::before {
  1281â†’  content: '';
  1282â†’  position: absolute;
  1283â†’  top: 50%;
  1284â†’  right: var(--spacing-4);
  1285â†’  width: 20px;
  1286â†’  height: 2px;
  1287â†’  background: var(--color-gray-400);
  1288â†’  transform: translateY(-50%);
  1289â†’  opacity: 0.5;
  1290â†’}
  1291â†’
  1292â†’.swipe-indicator::after {
  1293â†’  content: '';
  1294â†’  position: absolute;
  1295â†’  top: 50%;
  1296â†’  right: var(--spacing-3);
  1297â†’  width: 0;
  1298â†’  height: 0;
  1299â†’  border-left: 6px solid var(--color-gray-400);
  1300â†’  border-top: 4px solid transparent;
  1301â†’  border-bottom: 4px solid transparent;
  1302â†’  transform: translateY(-50%);
  1303â†’  opacity: 0.5;
  1304â†’}
  1305â†’
  1306â†’/* Loading states optimized for mobile */
  1307â†’.mobile-loading {
  1308â†’  display: flex;
  1309â†’  flex-direction: column;
  1310â†’  align-items: center;
  1311â†’  justify-content: center;
  1312â†’  min-height: 200px;
  1313â†’  padding: var(--spacing-8);
  1314â†’}
  1315â†’
  1316â†’.mobile-loading .loading-spinner {
  1317â†’  width: 40px;
  1318â†’  height: 40px;
  1319â†’  border: 3px solid var(--color-gray-200);
  1320â†’  border-top: 3px solid var(--color-primary-500);
  1321â†’  border-radius: 50%;
  1322â†’  animation: spin 1s linear infinite;
  1323â†’}
  1324â†’
  1325â†’.mobile-loading .loading-text {
  1326â†’  margin-top: var(--spacing-4);
  1327â†’  font-size: var(--font-size-sm);
  1328â†’  color: var(--color-text-secondary);
  1329â†’  text-align: center;
  1330â†’}
  1331â†’
  1332â†’/* Battery optimization styles */
  1333â†’.battery-optimize {
  1334â†’  animation-play-state: paused !important;
  1335â†’}
  1336â†’
  1337â†’.battery-optimize * {
