     1â†’/**
     2â†’ * Component Layer Styles (Converted from Tailwind Plugins)
     3â†’ *
     4â†’ * This file contains component base styles that were previously defined
     5â†’ * as JavaScript plugins. Converted to pure CSS for Tailwind v4 best practices.
     6â†’ *
     7â†’ * Includes:
     8â†’ * - Form controls (.form-control-base)
     9â†’ * - Buttons (.btn-base, .btn-sm, .btn-md, .btn-lg, .btn-xl)
    10â†’ * - Cards (.card-base, .card-interactive)
    11â†’ * - Badges (.badge-base)
    12â†’ * - Modals (.modal-backdrop, .modal-panel)
    13â†’ * - Input groups (.input-group)
    14â†’ */
    15â†’
    16â†’@layer components {
    17â†’  /* ==========================================
    18â†’   * FORM CONTROL BASE
    19â†’   * ========================================== */
    20â†’  .form-control-base {
    21â†’    width: 100%;
    22â†’    padding: var(--spacing-2) var(--spacing-4);
    23â†’    border: 1px solid var(--color-border-primary, var(--color-neutral-300));
    24â†’    border-radius: var(--radius-lg);
    25â†’    background-color: var(--color-surface-primary, #ffffff);
    26â†’    color: var(--color-neutral-900);
    27â†’    font-size: var(--font-size-base);
    28â†’    line-height: var(--line-height-normal);
    29â†’    transition: border-color 150ms ease, box-shadow 150ms ease;
    30â†’  }
    31â†’
    32â†’  .form-control-base::placeholder {
    33â†’    color: var(--color-neutral-400);
    34â†’  }
    35â†’
    36â†’  .form-control-base:focus {
    37â†’    outline: none;
    38â†’    border-color: var(--color-border-focus, var(--color-primary-500));
    39â†’    box-shadow: var(--shadow-focus-primary);
    40â†’  }
    41â†’
    42â†’  .form-control-base:disabled {
    43â†’    opacity: 0.6;
    44â†’    cursor: not-allowed;
    45â†’    background-color: var(--color-neutral-100);
    46â†’    color: var(--color-neutral-500);
    47â†’  }
    48â†’
    49â†’  .form-control-base[aria-invalid="true"] {
    50â†’    border-color: var(--color-border-error, var(--color-error-500));
    51â†’  }
    52â†’
    53â†’  .form-control-base[aria-invalid="true"]:focus {
    54â†’    box-shadow: var(--shadow-focus-error);
    55â†’  }
    56â†’
    57â†’  /* Dark mode support */
    58â†’  [data-theme="dark"] .form-control-base {
    59â†’    background-color: var(--color-dark-surface-primary);
    60â†’    border-color: var(--color-dark-border-primary);
    61â†’    color: var(--color-dark-text-primary);
    62â†’  }
    63â†’
    64â†’  [data-theme="dark"] .form-control-base::placeholder {
    65â†’    color: var(--color-neutral-500);
    66â†’  }
    67â†’
    68â†’  [data-theme="dark"] .form-control-base:disabled {
    69â†’    background-color: var(--color-dark-surface-secondary);
    70â†’    color: var(--color-neutral-600);
    71â†’  }
    72â†’
    73â†’  /* ==========================================
    74â†’   * BUTTON BASE
    75â†’   * ========================================== */
    76â†’  .btn-base {
    77â†’    display: inline-flex;
    78â†’    align-items: center;
    79â†’    justify-content: center;
    80â†’    gap: var(--spacing-2);
    81â†’    font-weight: var(--font-weight-medium);
    82â†’    border-radius: var(--radius-lg);
    83â†’    transition: all 150ms ease;
    84â†’    cursor: pointer;
    85â†’    text-decoration: none;
    86â†’    white-space: nowrap;
    87â†’    user-select: none;
    88â†’  }
    89â†’
    90â†’  .btn-base:disabled,
    91â†’  .btn-base[aria-disabled="true"] {
    92â†’    opacity: 0.6;
    93â†’    pointer-events: none;
    94â†’    cursor: not-allowed;
    95â†’  }
    96â†’
    97â†’  .btn-base:focus-visible {
    98â†’    outline: none;
    99â†’    box-shadow: var(--shadow-focus-primary);
   100â†’  }
   101â†’
   102â†’  /* Button size variants */
   103â†’  .btn-xs {
   104â†’    padding: var(--spacing-1) var(--spacing-2);
   105â†’    font-size: var(--font-size-xs);
   106â†’    line-height: var(--line-height-tight);
   107â†’  }
   108â†’
   109â†’  .btn-sm {
   110â†’    padding: var(--spacing-1_5) var(--spacing-3);
   111â†’    font-size: var(--font-size-sm);
   112â†’    line-height: var(--line-height-tight);
   113â†’  }
   114â†’
   115â†’  .btn-md {
   116â†’    padding: var(--spacing-2) var(--spacing-4);
   117â†’    font-size: var(--font-size-base);
   118â†’    line-height: var(--line-height-tight);
   119â†’  }
   120â†’
   121â†’  .btn-lg {
   122â†’    padding: var(--spacing-2_5) var(--spacing-6);
   123â†’    font-size: var(--font-size-lg);
   124â†’    line-height: var(--line-height-tight);
   125â†’  }
   126â†’
   127â†’  .btn-xl {
   128â†’    padding: var(--spacing-3) var(--spacing-8);
   129â†’    font-size: var(--font-size-xl);
   130â†’    line-height: var(--line-height-tight);
   131â†’  }
   132â†’
