/**
 * Component Layer Styles (Converted from Tailwind Plugins)
 *
 * This file contains component base styles that were previously defined
 * as JavaScript plugins. Converted to pure CSS for Tailwind v4 best practices.
 *
 * Includes:
 * - Form controls (.form-control-base)
 * - Buttons (.btn-base, .btn-sm, .btn-md, .btn-lg, .btn-xl)
 * - Cards (.card-base, .card-interactive)
 * - Badges (.badge-base)
 * - Modals (.modal-backdrop, .modal-panel)
 * - Input groups (.input-group)
 */

@layer components {
  /* ==========================================
   * FORM CONTROL BASE
   * ========================================== */
  .form-control-base {
    width: 100%;
    padding: var(--spacing-2) var(--spacing-4);
    border: 1px solid var(--color-border-primary, var(--color-neutral-300));
    border-radius: var(--radius-lg);
    background-color: var(--color-surface-primary, #ffffff);
    color: var(--color-neutral-900);
    font-size: var(--font-size-base);
    line-height: var(--line-height-normal);
    transition: border-color 150ms ease, box-shadow 150ms ease;
  }

  .form-control-base::placeholder {
    color: var(--color-neutral-400);
  }

  .form-control-base:focus {
    outline: none;
    border-color: var(--color-border-focus, var(--color-primary-500));
    box-shadow: var(--shadow-focus-primary);
  }

  .form-control-base:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    background-color: var(--color-neutral-100);
    color: var(--color-neutral-500);
  }

  .form-control-base[aria-invalid="true"] {
    border-color: var(--color-border-error, var(--color-error-500));
  }

  .form-control-base[aria-invalid="true"]:focus {
    box-shadow: var(--shadow-focus-error);
  }

  /* Dark mode support */
  [data-theme="dark"] .form-control-base {
    background-color: var(--color-dark-surface-primary);
    border-color: var(--color-dark-border-primary);
    color: var(--color-dark-text-primary);
  }

  [data-theme="dark"] .form-control-base::placeholder {
    color: var(--color-neutral-500);
  }

  [data-theme="dark"] .form-control-base:disabled {
    background-color: var(--color-dark-surface-secondary);
    color: var(--color-neutral-600);
  }

  /* ==========================================
   * BUTTON BASE
   * ========================================== */
  .btn-base {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: var(--spacing-2);
    font-weight: var(--font-weight-medium);
    border-radius: var(--radius-lg);
    transition: all 150ms ease;
    cursor: pointer;
    text-decoration: none;
    white-space: nowrap;
    user-select: none;
  }

  .btn-base:disabled,
  .btn-base[aria-disabled="true"] {
    opacity: 0.6;
    pointer-events: none;
    cursor: not-allowed;
  }

  .btn-base:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }

  /* Button size variants */
  .btn-xs {
    padding: var(--spacing-1) var(--spacing-2);
    font-size: var(--font-size-xs);
    line-height: var(--line-height-tight);
  }

  .btn-sm {
    padding: var(--spacing-1_5) var(--spacing-3);
    font-size: var(--font-size-sm);
    line-height: var(--line-height-tight);
  }

  .btn-md {
    padding: var(--spacing-2) var(--spacing-4);
    font-size: var(--font-size-base);
    line-height: var(--line-height-tight);
  }

  .btn-lg {
    padding: var(--spacing-2_5) var(--spacing-6);
    font-size: var(--font-size-lg);
    line-height: var(--line-height-tight);
  }

  .btn-xl {
    padding: var(--spacing-3) var(--spacing-8);
    font-size: var(--font-size-xl);
    line-height: var(--line-height-tight);
  }

  /* ==========================================
   * CARD BASE
   * ========================================== */
  .card-base {
    background-color: var(--color-surface-primary, #ffffff);
    border: 1px solid var(--color-border-primary, var(--color-neutral-200));
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-sm);
    transition: box-shadow 200ms ease, transform 200ms ease;
  }

  .card-base.card-interactive {
    cursor: pointer;
  }

  .card-base.card-interactive:hover {
    box-shadow: var(--shadow-md);
    transform: translateY(-2px);
  }

  .card-base.card-interactive:active {
    transform: translateY(0);
    box-shadow: var(--shadow-sm);
  }

  /* Card size variants */
  .card-sm {
    padding: var(--spacing-4);
  }

  .card-md {
    padding: var(--spacing-6);
  }

  .card-lg {
    padding: var(--spacing-8);
  }

  /* Dark mode support */
  [data-theme="dark"] .card-base {
    background-color: var(--color-dark-surface-primary);
    border-color: var(--color-dark-border-primary);
  }

  /* ==========================================
   * BADGE BASE
   * ========================================== */
  .badge-base {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-1);
    padding: var(--spacing-1) var(--spacing-2);
    font-size: var(--font-size-xs);
    font-weight: var(--font-weight-medium);
    text-transform: uppercase;
    border-radius: var(--radius-base);
    letter-spacing: 0.05em;
    line-height: var(--line-height-tight);
    white-space: nowrap;
  }

  /* Badge size variants */
  .badge-sm {
    padding: calc(var(--spacing-1) * 0.5) var(--spacing-1_5);
    font-size: 0.625rem; /* 10px */
  }

  .badge-md {
    padding: var(--spacing-1) var(--spacing-2);
    font-size: var(--font-size-xs);
  }

  .badge-lg {
    padding: var(--spacing-1_5) var(--spacing-3);
    font-size: var(--font-size-sm);
  }

  /* ==========================================
   * MODAL BASE
   * ========================================== */
  .modal-backdrop {
    position: fixed;
    inset: 0;
    background-color: rgb(0 0 0 / 0.5);
    backdrop-filter: blur(4px);
    z-index: var(--z-modal-backdrop);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: var(--spacing-4);
  }

  .modal-panel {
    background-color: var(--color-surface-primary, #ffffff);
    border-radius: var(--radius-2xl);
    box-shadow: var(--shadow-2xl);
    max-width: 90vw;
    max-height: 90vh;
    overflow: auto;
    position: relative;
  }

  [data-theme="dark"] .modal-panel {
    background-color: var(--color-dark-surface-primary);
  }

  /* ==========================================
   * INPUT GROUP (Icon + Input pattern)
   * ========================================== */
  .input-group {
    position: relative;
    width: 100%;
  }

  .input-group .input-group-icon {
    position: absolute;
    left: var(--spacing-3);
    top: 50%;
    transform: translateY(-50%);
    pointer-events: none;
    color: var(--color-neutral-400);
  }

  .input-group .input-group-input {
    padding-left: var(--spacing-10);
  }

  .input-group:focus-within .input-group-icon {
    color: var(--color-primary-500);
  }
}

