/** @type {import('tailwindcss').Config} */
export default {
  content: [
    './src/**/*.{astro,html,js,jsx,md,mdx,svelte,ts,tsx,vue}'
  ],
  // Plugins converted to pure CSS in src/styles/components.css and src/styles/utilities.css
  // This follows Tailwind v4 best practices for CSS-first configuration
  plugins: [],
};

import { defineConfig } from "astro/config";
import vue from "@astrojs/vue";
import { env } from "./src/env";
import cloudflare from "@astrojs/cloudflare";

// VITE PLUGINS
import tsConfigPaths from "vite-tsconfig-paths";
// import node from "@astrojs/node";

import icon from "astro-icon";
import removeConsole from "vite-plugin-remove-console";
import tailwindcss from "@tailwindcss/vite";
// import sentry from "@sentry/astro";
import { nodePolyfills } from "vite-plugin-node-polyfills";

import partytown from "@astrojs/partytown";
import sitemap from "@astrojs/sitemap";

// https://astro.build/config
export default defineConfig({
  site: import.meta.env.DEV
    ? `http://localhost:${process.env.PORT || 6942}`
    : "https://socialaize.com",
  output: "server",
  adapter: cloudflare({
    imageService: "passthrough",
    sessionKVBindingName: "SOCIALAIZE",
  }),
  vite: {
    plugins: [tsConfigPaths(), tailwindcss(), removeConsole(), nodePolyfills()],
    // build: { // DEBUG ONLY
    //   minify: false,
    // },
  },
  server: {
    port: import.meta.env.DEV ? parseInt(process.env.PORT || "6942") : undefined,
  },
  env: env,
  integrations: [
    vue({
      appEntrypoint: "src/_vueEntrypoint.ts",
    }),
    icon({
      include: {
        "simple-icons": [
          "discord",
          "facebook",
          "instagram",
          "linkedin",
          "mastodon",
          "pinterest",
          "reddit",
          "tiktok",
          "threads",
          "x",
          "youtube",
        ],
        mdi: [
          "account-group",
          "auto-fix",
          "content-copy",
          "swap-horizontal",
          "arrow-right",
          "account-voice",
          "chart-line",
          "calendar-clock",
          "trending-up",
          "refresh-auto",
          "image-edit",
          "image-multiple",
          "rocket-launch",
        ],
      },
    }),
    partytown(),
    sitemap({
      filter(page) {
        return !page.includes("api") && !page.includes("admin");
      },
    }),
  ],
});

