@import "tailwindcss";

/* Custom brand color palette */
@theme {
  --color-brand-50: #b3b9ff;
  --color-brand-100: #aaafff;
  --color-brand-200: #989dff;
  --color-brand-300: #878bff;
  --color-brand-400: #7578ff;
  --color-brand-500: #6366f1;
  --color-brand-600: #5356ca;
  --color-brand-700: #4345a4;
  --color-brand-800: #33357d;
  --color-brand-900: #242557;
  --color-brand-950: #1c1d43;
}

/* Base styles */
html {
  scroll-behavior: smooth;
}

body {
  font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
}

/* Dark mode defaults */
@media (prefers-color-scheme: dark) {
  html {
    color-scheme: dark;
  }
}

import { type ClassValue, clsx } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

