/**
 * Socialaize Design System Foundation
 * 
 * A comprehensive design token system for consistent theming,
 * components, and user experiences across the platform.
 */

:root {
  /* ===== COLOR PALETTE ===== */

  /* Primary Brand Colors */
  --color-primary-50: #e6f3f4;
  --color-primary-100: #cce5e7;
  --color-primary-200: #99cbd0;
  --color-primary-300: #66b0b8;
  --color-primary-400: #3396a1;
  --color-primary-500: #007c89;
  --color-primary-600: #00636e;
  --color-primary-700: #004a52;
  --color-primary-800: #003237;
  --color-primary-900: #00191b;
  --color-primary-950: #000d0e;

  /* Semantic Colors - Success */
  --color-success-50: #ecfdf5;
  --color-success-100: #d1fae5;
  --color-success-200: #a7f3d0;
  --color-success-300: #6ee7b7;
  --color-success-400: #34d399;
  --color-success-500: #10b981;
  --color-success-600: #059669;
  --color-success-700: #047857;
  --color-success-800: #065f46;
  --color-success-900: #064e3b;
  --color-success-950: #022c22;

  /* Semantic Colors - Warning */
  --color-warning-50: #fffbeb;
  --color-warning-100: #fef3c7;
  --color-warning-200: #fde68a;
  --color-warning-300: #fcd34d;
  --color-warning-400: #fbbf24;
  --color-warning-500: #f59e0b;
  --color-warning-600: #d97706;
  --color-warning-700: #b45309;
  --color-warning-800: #92400e;
  --color-warning-900: #78350f;
  --color-warning-950: #451a03;

  /* Semantic Colors - Error/Danger */
  --color-error-50: #fef2f2;
  --color-error-100: #fee2e2;
  --color-error-200: #fecaca;
  --color-error-300: #fca5a5;
  --color-error-400: #f87171;
  --color-error-500: #ef4444;
  --color-error-600: #dc2626;
  --color-error-700: #b91c1c;
  --color-error-800: #991b1b;
  --color-error-900: #7f1d1d;
  --color-error-950: #450a0a;

  /* Semantic Colors - Info */
  --color-info-50: #eff6ff;
  --color-info-100: #dbeafe;
  --color-info-200: #bfdbfe;
  --color-info-300: #93c5fd;
  --color-info-400: #60a5fa;
  --color-info-500: #3b82f6;
  --color-info-600: #2563eb;
  --color-info-700: #1d4ed8;
  --color-info-800: #1e40af;
  --color-info-900: #1e3a8a;
  --color-info-950: #172554;

  /* Neutral Grays */
  --color-gray-50: #f9fafb;
  --color-gray-100: #f3f4f6;
  --color-gray-200: #e5e7eb;
  --color-gray-300: #d1d5db;
  --color-gray-400: #9ca3af;
  --color-gray-500: #6b7280;
  --color-gray-600: #4b5563;
  --color-gray-700: #374151;
  --color-gray-800: #1f2937;
  --color-gray-900: #111827;
  --color-gray-950: #030712;

  /* Surface Colors */
  --color-surface-primary: #ffffff;
  --color-surface-secondary: var(--color-gray-50);
  --color-surface-tertiary: var(--color-gray-100);
  --color-surface-inverse: var(--color-gray-900);

  /* Border Colors */
  --color-border-primary: var(--color-gray-200);
  --color-border-secondary: var(--color-gray-300);
  --color-border-focus: var(--color-primary-500);
  --color-border-error: var(--color-error-500);
  --color-border-success: var(--color-success-500);

  /* Text Colors */
  --color-text-primary: var(--color-gray-900);
  --color-text-secondary: var(--color-gray-600);
  --color-text-tertiary: var(--color-gray-500);
  --color-text-inverse: #ffffff;
  --color-text-placeholder: var(--color-gray-400);

  /* ===== DARK MODE COLORS ===== */
  --color-dark-surface-primary: var(--color-gray-800);
  --color-dark-surface-secondary: var(--color-gray-900);
  --color-dark-surface-tertiary: var(--color-gray-950);
  --color-dark-surface-inverse: #ffffff;

  --color-dark-border-primary: var(--color-gray-700);
  --color-dark-border-secondary: var(--color-gray-600);
  --color-dark-border-focus: var(--color-primary-400);

  --color-dark-text-primary: var(--color-gray-100);
  --color-dark-text-secondary: var(--color-gray-300);
  --color-dark-text-tertiary: var(--color-gray-400);
  --color-dark-text-placeholder: var(--color-gray-500);

  /* ===== SPACING SYSTEM ===== */
  --spacing-0: 0;
  --spacing-px: 1px;
  --spacing-0_5: 0.125rem;  /* 2px */
  --spacing-1: 0.25rem;     /* 4px */
  --spacing-1_5: 0.375rem;  /* 6px */
  --spacing-2: 0.5rem;      /* 8px */
  --spacing-2_5: 0.625rem;  /* 10px */
  --spacing-3: 0.75rem;     /* 12px */
  --spacing-3_5: 0.875rem;  /* 14px */
  --spacing-4: 1rem;        /* 16px */
  --spacing-5: 1.25rem;     /* 20px */
  --spacing-6: 1.5rem;      /* 24px */
  --spacing-7: 1.75rem;     /* 28px */
  --spacing-8: 2rem;        /* 32px */
  --spacing-9: 2.25rem;     /* 36px */
  --spacing-10: 2.5rem;     /* 40px */
  --spacing-11: 2.75rem;    /* 44px */
  --spacing-12: 3rem;       /* 48px */
  --spacing-14: 3.5rem;     /* 56px */
  --spacing-16: 4rem;       /* 64px */
  --spacing-20: 5rem;       /* 80px */
  --spacing-24: 6rem;       /* 96px */
  --spacing-28: 7rem;       /* 112px */
  --spacing-32: 8rem;       /* 128px */

  /* ===== BORDER RADIUS ===== */
  --radius-none: 0;
  --radius-sm: 0.125rem;    /* 2px */
  --radius-base: 0.25rem;   /* 4px */
  --radius-md: 0.375rem;    /* 6px */
  --radius-lg: 0.5rem;      /* 8px */
  --radius-xl: 0.75rem;     /* 12px */
  --radius-2xl: 1rem;       /* 16px */
  --radius-3xl: 1.5rem;     /* 24px */
  --radius-full: 9999px;

  /* ===== SHADOWS ===== */
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
  --shadow-inner: inset 0 2px 4px 0 rgb(0 0 0 / 0.05);

  /* Focus Shadows */
  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.1);
  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.1);
  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.1);

  /* ===== TRANSITIONS ===== */
  --transition-fast: 150ms ease;
  --transition-base: 200ms ease;
  --transition-medium: 300ms ease;
  --transition-slow: 500ms ease;

  /* Easing Functions */
  --ease-linear: linear;
  --ease-in: cubic-bezier(0.4, 0, 1, 1);
  --ease-out: cubic-bezier(0, 0, 0.2, 1);
  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);

  /* ===== BACKGROUND GRADIENTS ===== */

  /* Hero/Landing Gradients - Matching original multi-radial design */
  --gradient-hero-base: linear-gradient(to bottom, #ffffff, #f8fafc);
  --gradient-hero-radial-1: radial-gradient(1100px 1100px at 8% 8%, rgba(99, 102, 241, 0.12), transparent 60%);
  --gradient-hero-radial-2: radial-gradient(1200px 1200px at 92% 92%, rgba(168, 85, 247, 0.11), transparent 60%);
  --gradient-hero-radial-3: radial-gradient(1300px 900px at -10% 50%, rgba(45, 212, 191, 0.08), transparent 65%);
  --gradient-hero-radial-4: radial-gradient(1300px 900px at 110% 45%, rgba(59, 130, 246, 0.08), transparent 65%);
  --gradient-hero-radial-5: radial-gradient(900px 900px at 95% 10%, rgba(236, 72, 153, 0.06), transparent 65%);

  /* Alternative simpler gradients */
  --gradient-hero-vibrant: linear-gradient(135deg, var(--color-primary-100) 0%, var(--color-primary-200) 100%);
  --gradient-hero-subtle: linear-gradient(180deg, var(--color-gray-50) 0%, var(--color-surface-primary) 100%);

  /* Accent Gradients */
  --gradient-accent-primary: linear-gradient(135deg, var(--color-primary-400) 0%, var(--color-primary-600) 100%);
  --gradient-accent-purple: linear-gradient(135deg, #c084fc 0%, #7c3aed 100%);
  --gradient-accent-warm: linear-gradient(135deg, #fb923c 0%, #f59e0b 100%);

  /* Feature Section Gradients */
  --gradient-feature-light: linear-gradient(to right, var(--color-primary-50), var(--color-gray-50));
  --gradient-feature-card: linear-gradient(to bottom, var(--color-surface-primary), var(--color-surface-secondary));

  /* CTA/Interactive Gradients */
  --gradient-cta: linear-gradient(135deg, var(--color-primary-500) 0%, var(--color-primary-600) 100%);
  --gradient-cta-hover: linear-gradient(135deg, var(--color-primary-600) 0%, var(--color-primary-700) 100%);

  /* Optional Mesh Gradient Overlay for Extra Depth */
  --gradient-mesh-overlay:
    radial-gradient(at 20% 30%, rgba(124, 58, 237, 0.08) 0px, transparent 50%),
    radial-gradient(at 80% 70%, rgba(59, 130, 246, 0.08) 0px, transparent 50%),
    radial-gradient(at 40% 80%, rgba(236, 72, 153, 0.06) 0px, transparent 50%);

  /* ===== TYPOGRAPHY ===== */
  --font-family-sans: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
  --font-family-mono: 'JetBrains Mono', 'SF Mono', Monaco, Inconsolata, 'Roboto Mono', 'Source Code Pro', monospace;
  --font-family-emoji: 'Noto Color Emoji', 'Apple Color Emoji', 'Segoe UI Emoji', sans-serif;

  /* Font Weights */
  --font-weight-thin: 100;
  --font-weight-extralight: 200;
  --font-weight-light: 300;
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-semibold: 600;
  --font-weight-bold: 700;
  --font-weight-extrabold: 800;
  --font-weight-black: 900;

  /* Font Sizes */
  --font-size-xs: 0.75rem;      /* 12px */
  --font-size-sm: 0.875rem;     /* 14px */
  --font-size-base: 1rem;       /* 16px */
  --font-size-lg: 1.125rem;     /* 18px */
  --font-size-xl: 1.25rem;      /* 20px */
  --font-size-2xl: 1.5rem;      /* 24px */
  --font-size-3xl: 1.875rem;    /* 30px */
  --font-size-4xl: 2.25rem;     /* 36px */
  --font-size-5xl: 3rem;        /* 48px */
  --font-size-6xl: 3.75rem;     /* 60px */
  --font-size-7xl: 4.5rem;      /* 72px */
  --font-size-8xl: 6rem;        /* 96px */
  --font-size-9xl: 8rem;        /* 128px */

  /* Line Heights */
  --line-height-none: 1;
  --line-height-tight: 1.25;
  --line-height-snug: 1.375;
  --line-height-normal: 1.5;
  --line-height-relaxed: 1.625;
  --line-height-loose: 2;

  /* ===== COMPONENT SIZES ===== */
  --size-xs: 1.5rem;    /* 24px */
  --size-sm: 2rem;      /* 32px */
  --size-md: 2.5rem;    /* 40px */
  --size-lg: 3rem;      /* 48px */
  --size-xl: 3.5rem;    /* 56px */
  --size-2xl: 4rem;     /* 64px */

  /* Input Heights */
  --input-height-sm: var(--size-sm);
  --input-height-md: var(--size-md);
  --input-height-lg: var(--size-lg);

  /* ===== Z-INDEX SCALE ===== */
  --z-index-behind: -1;
  --z-index-base: 0;
  --z-index-chat: 900;
  --z-index-dropdown: 1000;
  --z-index-notification: 1000;
  --z-index-sticky: 1020;
  --z-index-fixed: 1030;
  --z-index-modal-backdrop: 1040;
  --z-index-modal: 1050;
  --z-index-popover: 1060;
  --z-index-tooltip: 1070;
  --z-index-toast: 1080;
  --z-index-max: 2147483647;

  /* ===== ANIMATION UTILITIES ===== */
  --animation-pulse: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  --animation-bounce: bounce 1s infinite;
  --animation-spin: spin 1s linear infinite;
  --animation-ping: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
  --animation-fade-in: fadeIn var(--transition-base);
  --animation-fade-out: fadeOut var(--transition-base);
  --animation-slide-in-up: slideInUp var(--transition-medium) var(--ease-out);
  --animation-slide-in-down: slideInDown var(--transition-medium) var(--ease-out);
  --animation-scale-in: scaleIn var(--transition-base) var(--ease-out);
  --animation-scale-out: scaleOut var(--transition-base) var(--ease-in);
}

/* ===== DARK MODE OVERRIDES ===== */
[data-theme="dark"] {
  --color-surface-primary: var(--color-dark-surface-primary);
  --color-surface-secondary: var(--color-dark-surface-secondary);
  --color-surface-tertiary: var(--color-dark-surface-tertiary);
  --color-surface-inverse: var(--color-dark-surface-inverse);

  --color-border-primary: var(--color-dark-border-primary);
  --color-border-secondary: var(--color-dark-border-secondary);
  --color-border-focus: var(--color-dark-border-focus);

  --color-text-primary: var(--color-dark-text-primary);
  --color-text-secondary: var(--color-dark-text-secondary);
  --color-text-tertiary: var(--color-dark-text-tertiary);
  --color-text-placeholder: var(--color-dark-text-placeholder);

  /* Enhanced shadows for dark mode */
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.2);
  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.3), 0 1px 2px -1px rgb(0 0 0 / 0.3);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.4), 0 2px 4px -2px rgb(0 0 0 / 0.4);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.5), 0 4px 6px -4px rgb(0 0 0 / 0.5);
  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.6), 0 8px 10px -6px rgb(0 0 0 / 0.6);
  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.7);

  /* Dark mode focus shadows */
  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.2);
  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.2);
  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.2);

  /* Dark Mode Gradient Overrides - Matching original dark theme */
  --gradient-hero-base: linear-gradient(to bottom, #0f172a, #111827);
  --gradient-hero-radial-1: radial-gradient(1100px 1100px at 8% 8%, rgba(99, 102, 241, 0.32), transparent 60%);
  --gradient-hero-radial-2: radial-gradient(1200px 1200px at 92% 92%, rgba(168, 85, 247, 0.28), transparent 60%);
  --gradient-hero-radial-3: radial-gradient(1300px 900px at -10% 50%, rgba(45, 212, 191, 0.20), transparent 65%);
  --gradient-hero-radial-4: radial-gradient(1300px 900px at 110% 45%, rgba(59, 130, 246, 0.20), transparent 65%);
  --gradient-hero-radial-5: radial-gradient(900px 900px at 95% 10%, rgba(236, 72, 153, 0.14), transparent 65%);

  --gradient-hero-vibrant: linear-gradient(135deg, var(--color-primary-800) 0%, var(--color-primary-700) 100%);
  --gradient-hero-subtle: linear-gradient(180deg, var(--color-gray-900) 0%, var(--color-dark-surface-primary) 100%);

  --gradient-accent-primary: linear-gradient(135deg, var(--color-primary-500) 0%, var(--color-primary-600) 100%);
  --gradient-accent-purple: linear-gradient(135deg, #a78bfa 0%, #7c3aed 100%);
  --gradient-accent-warm: linear-gradient(135deg, #fb923c 0%, #f59e0b 100%);

  --gradient-feature-light: linear-gradient(to right, var(--color-primary-900), var(--color-gray-900));
  --gradient-feature-card: linear-gradient(to bottom, var(--color-dark-surface-primary), var(--color-dark-surface-secondary));

  --gradient-cta: linear-gradient(135deg, var(--color-primary-600) 0%, var(--color-primary-700) 100%);
  --gradient-cta-hover: linear-gradient(135deg, var(--color-primary-500) 0%, var(--color-primary-600) 100%);

  --gradient-mesh-overlay:
    radial-gradient(at 20% 30%, rgba(124, 58, 237, 0.12) 0px, transparent 50%),
    radial-gradient(at 80% 70%, rgba(59, 130, 246, 0.12) 0px, transparent 50%),
    radial-gradient(at 40% 80%, rgba(236, 72, 153, 0.10) 0px, transparent 50%);
}

/* ===== KEYFRAME ANIMATIONS ===== */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes fadeOut {
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
}

@keyframes slideInUp {
  from {
    opacity: 0;
    transform: translateY(1rem);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInDown {
  from {
    opacity: 0;
    transform: translateY(-1rem);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes scaleOut {
  from {
    opacity: 1;
    transform: scale(1);
  }
  to {
    opacity: 0;
    transform: scale(0.9);
  }
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

@keyframes bounce {
  0%, 100% {
    transform: translateY(-25%);
    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
  }
  50% {
    transform: none;
    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
  }
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

@keyframes ping {
  75%, 100% {
    transform: scale(2);
    opacity: 0;
  }
}

/* ===== UTILITY CLASSES ===== */

/* Animation utilities */
.animate-fade-in {
  animation: var(--animation-fade-in);
}

.animate-fade-out {
  animation: var(--animation-fade-out);
}

.animate-slide-in-up {
  animation: var(--animation-slide-in-up);
}

.animate-slide-in-down {
  animation: var(--animation-slide-in-down);
}

.animate-scale-in {
  animation: var(--animation-scale-in);
}

.animate-scale-out {
  animation: var(--animation-scale-out);
}

.animate-pulse {
  animation: var(--animation-pulse);
}

.animate-bounce {
  animation: var(--animation-bounce);
}

.animate-spin {
  animation: var(--animation-spin);
}

.animate-ping {
  animation: var(--animation-ping);
}

/* Transition utilities */
.transition-fast {
  transition: all var(--transition-fast);
}

.transition-base {
  transition: all var(--transition-base);
}

.transition-medium {
  transition: all var(--transition-medium);
}

.transition-slow {
  transition: all var(--transition-slow);
}

/* Focus utilities */
.focus-ring {
  outline: 2px solid transparent;
  outline-offset: 2px;
}

.focus-ring:focus-visible {
  box-shadow: var(--shadow-focus-primary);
}

.focus-ring-error:focus-visible {
  box-shadow: var(--shadow-focus-error);
}

.focus-ring-success:focus-visible {
  box-shadow: var(--shadow-focus-success);
}

/* Glass morphism utilities */
.glass-subtle {
  background: rgb(255 255 255 / 0.8);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
}

[data-theme="dark"] .glass-subtle {
  background: rgb(17 24 39 / 0.8);
}

.glass-medium {
  background: rgb(255 255 255 / 0.9);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
}

[data-theme="dark"] .glass-medium {
  background: rgb(17 24 39 / 0.9);
}

.glass-strong {
  background: rgb(255 255 255 / 0.95);
  backdrop-filter: blur(16px);
  -webkit-backdrop-filter: blur(16px);
}

[data-theme="dark"] .glass-strong {
  background: rgb(17 24 39 / 0.95);
}

/* ===== COMPONENT BASE STYLES ===== */

/* Form controls base */
.form-control-base {
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-lg);
  background-color: var(--color-surface-primary);
  color: var(--color-text-primary);
  font-size: var(--font-size-base);
  font-weight: var(--font-weight-normal);
  line-height: var(--line-height-normal);
  transition: all var(--transition-base);
}

.form-control-base:focus {
  outline: none;
  border-color: var(--color-border-focus);
  box-shadow: var(--shadow-focus-primary);
}

.form-control-base:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  background-color: var(--color-surface-secondary);
}

.form-control-base.error {
  border-color: var(--color-border-error);
}

.form-control-base.error:focus {
  box-shadow: var(--shadow-focus-error);
}

.form-control-base.success {
  border-color: var(--color-border-success);
}

.form-control-base.success:focus {
  box-shadow: var(--shadow-focus-success);
}

/* Button base styles */
.btn-base {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-weight: var(--font-weight-medium);
  text-align: center;
  vertical-align: middle;
  user-select: none;
  border: 1px solid transparent;
  border-radius: var(--radius-lg);
  transition: all var(--transition-base);
  cursor: pointer;
  text-decoration: none;
  line-height: var(--line-height-tight);
  position: relative;
  overflow: hidden;
}

.btn-base:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  pointer-events: none;
}

.btn-base:focus-visible {
  outline: none;
  box-shadow: var(--shadow-focus-primary);
}

/* Button sizes */
.btn-sm {
  height: var(--size-sm);
  padding: 0 var(--spacing-3);
  font-size: var(--font-size-sm);
  gap: var(--spacing-2);
}

.btn-md {
  height: var(--size-md);
  padding: 0 var(--spacing-4);
  font-size: var(--font-size-base);
  gap: var(--spacing-2);
}

.btn-lg {
  height: var(--size-lg);
  padding: 0 var(--spacing-6);
  font-size: var(--font-size-lg);
  gap: var(--spacing-3);
}

/* Card base styles */
.card-base {
  background-color: var(--color-surface-primary);
  border: 1px solid var(--color-border-primary);
  border-radius: var(--radius-xl);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-base);
}

.card-base:hover {
  box-shadow: var(--shadow-md);
}

/* Badge base styles */
.badge-base {
  display: inline-flex;
  align-items: center;
  padding: var(--spacing-1) var(--spacing-2);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-medium);
  line-height: var(--line-height-tight);
  border-radius: var(--radius-base);
  text-transform: uppercase;
  letter-spacing: 0.025em;
}

/* Modal backdrop styles */
.modal-backdrop {
  position: fixed;
  inset: 0;
  background-color: rgb(0 0 0 / 0.5);
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
  z-index: var(--z-index-modal-backdrop);
}

[data-theme="dark"] .modal-backdrop {
  background-color: rgb(0 0 0 / 0.7);
}

/* Responsive utilities */
@media (max-width: 640px) {
  .responsive-text {
    font-size: calc(var(--font-size-base) * 0.9);
  }

  .responsive-spacing {
    padding: var(--spacing-4);
  }
}

@media (min-width: 1024px) {
  .responsive-text {
    font-size: calc(var(--font-size-base) * 1.1);
  }
}

/* ===== ACCESSIBILITY ENHANCEMENTS ===== */

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .form-control-base {
    border-width: 2px;
  }

  .btn-base {
    border-width: 2px;
  }
}

/* Focus visible improvements */
@media (prefers-reduced-motion: no-preference) {
  .focus-visible-enhance:focus-visible {
    transform: scale(1.02);
  }
}

/* ===== REAL-TIME & NOTIFICATION COMPONENTS ===== */

/* Glass morphism effects */
.glass-none {
  background: var(--color-surface-primary);
  border: 1px solid var(--color-border-primary);
}

.glass-subtle {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.glass-medium {
  background: rgba(255, 255, 255, 0.85);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.3);
}

.glass-strong {
  background: rgba(255, 255, 255, 0.75);
  backdrop-filter: blur(30px);
  -webkit-backdrop-filter: blur(30px);
  border: 1px solid rgba(255, 255, 255, 0.4);
}

[data-theme="dark"] .glass-subtle {
  background: rgba(17, 24, 39, 0.95);
  border: 1px solid rgba(75, 85, 99, 0.2);
}

[data-theme="dark"] .glass-medium {
  background: rgba(17, 24, 39, 0.85);
  border: 1px solid rgba(75, 85, 99, 0.3);
}

[data-theme="dark"] .glass-strong {
  background: rgba(17, 24, 39, 0.75);
  border: 1px solid rgba(75, 85, 99, 0.4);
}

/* Screen reader only content */
.sr-only {
  position: absolute !important;
  width: 1px !important;
  height: 1px !important;
  padding: 0 !important;
  margin: -1px !important;
  overflow: hidden !important;
  clip: rect(0, 0, 0, 0) !important;
  white-space: nowrap !important;
  border: 0 !important;
}

/* Skip link for keyboard navigation */
.skip-link {
  position: absolute;
  left: -10000px;
  top: auto;
  width: 1px;
  height: 1px;
  overflow: hidden;
  z-index: 999999;
  padding: var(--spacing-2) var(--spacing-4);
  background: var(--color-primary-500);
  color: white;
  text-decoration: none;
  border-radius: var(--radius-base);
  font-weight: var(--font-weight-medium);
}

.skip-link:focus {
  position: fixed !important;
  left: var(--spacing-2) !important;
  top: var(--spacing-2) !important;
  width: auto !important;
  height: auto !important;
  overflow: visible !important;
}

/* Real-time connection indicators */
.connection-indicator {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--radius-full);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-medium);
}

.connection-indicator--connected {
  background: var(--color-success-100);
  color: var(--color-success-700);
  border: 1px solid var(--color-success-200);
}

.connection-indicator--connecting {
  background: var(--color-warning-100);
  color: var(--color-warning-700);
  border: 1px solid var(--color-warning-200);
}

.connection-indicator--disconnected {
  background: var(--color-error-100);
  color: var(--color-error-700);
  border: 1px solid var(--color-error-200);
}

[data-theme="dark"] .connection-indicator--connected {
  background: var(--color-success-950);
  color: var(--color-success-300);
  border: 1px solid var(--color-success-800);
}

[data-theme="dark"] .connection-indicator--connecting {
  background: var(--color-warning-950);
  color: var(--color-warning-300);
  border: 1px solid var(--color-warning-800);
}

[data-theme="dark"] .connection-indicator--disconnected {
  background: var(--color-error-950);
  color: var(--color-error-300);
  border: 1px solid var(--color-error-800);
}

/* Notification badges */
.notification-badge {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 1.25rem;
  height: 1.25rem;
  padding: 0 0.25rem;
  font-size: 0.75rem;
  font-weight: var(--font-weight-bold);
  line-height: 1;
  border-radius: var(--radius-full);
  white-space: nowrap;
}

.notification-badge--primary {
  background: var(--color-primary-500);
  color: white;
}

.notification-badge--error {
  background: var(--color-error-500);
  color: white;
}

.notification-badge--warning {
  background: var(--color-warning-500);
  color: white;
}

.notification-badge--success {
  background: var(--color-success-500);
  color: white;
}

/* Performance optimizations */
.will-change-transform {
  will-change: transform;
}

.will-change-opacity {
  will-change: opacity;
}

.gpu-acceleration {
  transform: translateZ(0);
  backface-visibility: hidden;
  perspective: 1000;
}

/* Battery optimization styles */
@media (max-width: 768px) {
  .battery-optimize {
    animation-duration: 0s !important;
    transition-duration: 0.1s !important;
  }

  .battery-optimize .glass-medium,
  .battery-optimize .glass-strong {
    backdrop-filter: none !important;
    -webkit-backdrop-filter: none !important;
    background: var(--color-surface-primary) !important;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  .notifications-container,
  .chat-widget-container,
  .connection-indicator {
    display: none !important;
  }

  * {
    background: transparent !important;
    color: black !important;
    box-shadow: none !important;
    text-shadow: none !important;
  }
}

/* ===== MOBILE OPTIMIZATIONS ===== */

/* Touch optimization styles */
.touch-optimized {
  -webkit-tap-highlight-color: transparent;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  user-select: none;
}

.touch-optimized button,
.touch-optimized [role="button"],
.touch-optimized a,
.touch-optimized input,
.touch-optimized select,
.touch-optimized textarea {
  -webkit-user-select: text;
  user-select: text;
}

/* Large touch targets for mobile */
.large-touch-targets button,
.large-touch-targets [role="button"],
.large-touch-targets a,
.large-touch-targets .btn-base {
  min-height: 44px;
  min-width: 44px;
  padding: var(--spacing-3) var(--spacing-4);
}

.large-touch-targets .btn-sm {
  min-height: 40px;
  min-width: 40px;
}

.large-touch-targets .btn-lg {
  min-height: 48px;
  min-width: 48px;
}

/* Performance mode optimizations */
.performance-mode * {
  will-change: auto !important;
  animation-duration: 0.1s !important;
  transition-duration: 0.1s !important;
}

.performance-mode .glass-medium,
.performance-mode .glass-strong {
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
  background: var(--color-surface-primary) !important;
}

/* Reduced animations for accessibility and battery */
.reduce-animations * {
  animation-duration: 0.01ms !important;
  animation-iteration-count: 1 !important;
  transition-duration: 0.01ms !important;
}

.reduce-animations .animate-pulse,
.reduce-animations .animate-bounce,
.reduce-animations .animate-spin {
  animation: none !important;
}

/* Mobile-specific breakpoints */
@media (max-width: 480px) {
  /* Extra small mobile phones */
  .responsive-mobile-xs {
    font-size: 14px;
    padding: var(--spacing-2);
  }

  .btn-base {
    font-size: var(--font-size-sm);
    padding: var(--spacing-2) var(--spacing-3);
  }

  .modal-backdrop {
    backdrop-filter: none;
    -webkit-backdrop-filter: none;
  }
}

@media (max-width: 640px) {
  /* Small mobile devices */
  .responsive-mobile-sm {
    padding: var(--spacing-3);
  }

  .card-base {
    border-radius: var(--radius-lg);
    margin: var(--spacing-2);
  }

  .glass-medium,
  .glass-strong {
    backdrop-filter: blur(8px) !important;
    -webkit-backdrop-filter: blur(8px) !important;
  }
}

/* Landscape mobile orientation */
@media (max-height: 500px) and (orientation: landscape) {
  .landscape-optimize {
    height: 100vh;
    overflow-y: auto;
  }

  .notifications-container {
    top: var(--spacing-2);
    right: var(--spacing-2);
  }

  .chat-widget-container {
    bottom: var(--spacing-2);
    right: var(--spacing-2);
  }
}

/* High DPI displays (retina) */
@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  .retina-optimize {
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
  }

  .retina-optimize img {
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
  }
}

/* Hover detection for touch devices */
@media (hover: none) {
  .hover-only {
    display: none !important;
  }

  .btn-base:hover {
    transform: none !important;
  }

  .card-base:hover {
    transform: none !important;
    box-shadow: var(--shadow-sm) !important;
  }
}

/* Pointer device optimization */
@media (pointer: coarse) {
  /* Touch devices */
  .form-control-base {
    font-size: 16px; /* Prevents zoom on iOS */
    padding: var(--spacing-3);
  }

  .btn-base {
    padding: var(--spacing-3) var(--spacing-4);
  }

  select,
  input,
  textarea {
    font-size: 16px; /* Prevents zoom on iOS */
  }
}

/* Safe area insets for devices with notches */
@supports (padding: max(0px)) {
  .safe-area-inset-top {
    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  }

  .safe-area-inset-bottom {
    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  }

  .safe-area-inset-left {
    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  }

  .safe-area-inset-right {
    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  }
}

/* PWA display modes */
@media (display-mode: standalone) {
  .pwa-standalone {
    padding-top: var(--spacing-4);
  }

  .pwa-standalone .navbar {
    padding-top: env(safe-area-inset-top);
  }
}

/* Connection-aware styles */
.slow-connection .glass-medium,
.slow-connection .glass-strong {
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
  background: var(--color-surface-primary) !important;
}

.slow-connection img {
  filter: blur(2px);
  transition: filter var(--transition-base);
}

.slow-connection img.loaded {
  filter: none;
}

/* Gesture feedback */
.gesture-feedback {
  position: relative;
  overflow: hidden;
}

.gesture-feedback::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(0, 0, 0, 0.1);
  transform: translate(-50%, -50%);
  transition: width 0.3s ease, height 0.3s ease;
  pointer-events: none;
}

.gesture-feedback.active::after {
  width: 100px;
  height: 100px;
}

/* Dark mode gesture feedback */
[data-theme="dark"] .gesture-feedback::after {
  background: rgba(255, 255, 255, 0.1);
}

/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */

/* Swipe gesture indicators */
.swipe-indicator {
  position: relative;
}

.swipe-indicator::before {
  content: '';
  position: absolute;
  top: 50%;
  right: var(--spacing-4);
  width: 20px;
  height: 2px;
  background: var(--color-gray-400);
  transform: translateY(-50%);
  opacity: 0.5;
}

.swipe-indicator::after {
  content: '';
  position: absolute;
  top: 50%;
  right: var(--spacing-3);
  width: 0;
  height: 0;
  border-left: 6px solid var(--color-gray-400);
  border-top: 4px solid transparent;
  border-bottom: 4px solid transparent;
  transform: translateY(-50%);
  opacity: 0.5;
}

/* Loading states optimized for mobile */
.mobile-loading {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 200px;
  padding: var(--spacing-8);
}

.mobile-loading .loading-spinner {
  width: 40px;
  height: 40px;
  border: 3px solid var(--color-gray-200);
  border-top: 3px solid var(--color-primary-500);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

.mobile-loading .loading-text {
  margin-top: var(--spacing-4);
  font-size: var(--font-size-sm);
  color: var(--color-text-secondary);
  text-align: center;
}

/* Battery optimization styles */
.battery-optimize {
  animation-play-state: paused !important;
}

.battery-optimize * {
  animation-play-state: paused !important;
  transition-duration: 0.1s !important;
}

.battery-optimize .glass-medium,
.battery-optimize .glass-strong {
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
  background: var(--color-surface-primary) !important;
}

.battery-optimize .animate-pulse,
.battery-optimize .animate-bounce,
.battery-optimize .animate-spin {
  animation: none !important;
}

/* Accessibility enhancements for mobile */
@media (max-width: 768px) {
  .mobile-a11y button,
  .mobile-a11y [role="button"],
  .mobile-a11y input,
  .mobile-a11y select {
    min-height: 44px;
    font-size: 16px;
  }

  .mobile-a11y .focus-ring:focus-visible {
    outline: 3px solid var(--color-primary-500);
    outline-offset: 2px;
  }
}

