const handleLogout = async () => {
  console.log("🚪 Starting logout process...");

  // Set logout flag IMMEDIATELY to prevent any auto-navigation
  const { isLoggingOut, recentLogout } = await import('@/stores/authStore');
  isLoggingOut.set(true);
  recentLogout.set(Date.now());

  await logout();

  showSuccess("Successfully logged out, redirecting to login...");

  // Force a full page reload to ensure complete state clearing
  // This prevents any cached JavaScript state from interfering
  setTimeout(() => {
    if (typeof window !== 'undefined') {
      window.location.href = '/login';
    } else {
      navigate('/login');
    }
  }, 500);
};

const goToDashboard = () => {
  navigate("/dashboard");
