const toggleTheme = async () => {
  if (mounted.value) {
    // Get the current actual theme from the document
    const actualTheme = document.documentElement.getAttribute("data-theme") || "light";

    // Calculate the new theme based on the actual current theme
    const newTheme = actualTheme === "dark" ? "light" : "dark";

    // Update the ref to match
    currentTheme.value = newTheme;

    // Apply the theme change to the document
    document.documentElement.setAttribute("data-theme", newTheme);

    // Dispatch the event for other components
    document.dispatchEvent(new CustomEvent("set-theme", { detail: newTheme }));

    // Force a re-render to ensure the icon updates correctly
    await nextTick();
  }
};
</script>

