  1080→/* Reduced animations for accessibility and battery */
  1081→.reduce-animations * {
  1082→  animation-duration: 0.01ms !important;
  1083→  animation-iteration-count: 1 !important;
  1084→  transition-duration: 0.01ms !important;
  1085→}
  1086→
  1087→.reduce-animations .animate-pulse,
  1088→.reduce-animations .animate-bounce,
  1089→.reduce-animations .animate-spin {
  1090→  animation: none !important;
  1091→}
  1092→
  1093→/* Mobile-specific breakpoints */
  1094→@media (max-width: 480px) {
  1095→  /* Extra small mobile phones */
  1096→  .responsive-mobile-xs {
  1097→    font-size: 14px;
  1098→    padding: var(--spacing-2);
  1099→  }
  1100→  
  1101→  .btn-base {
  1102→    font-size: var(--font-size-sm);
  1103→    padding: var(--spacing-2) var(--spacing-3);
  1104→  }
  1105→  
  1106→  .modal-backdrop {
  1107→    backdrop-filter: none;
  1108→    -webkit-backdrop-filter: none;
  1109→  }
  1110→}
  1111→
  1112→@media (max-width: 640px) {
  1113→  /* Small mobile devices */
  1114→  .responsive-mobile-sm {
  1115→    padding: var(--spacing-3);
  1116→  }
  1117→  
  1118→  .card-base {
  1119→    border-radius: var(--radius-lg);
  1120→    margin: var(--spacing-2);
  1121→  }
  1122→  
  1123→  .glass-medium,
  1124→  .glass-strong {
  1125→    backdrop-filter: blur(8px) !important;
  1126→    -webkit-backdrop-filter: blur(8px) !important;
  1127→  }
  1128→}
  1129→
  1130→/* Landscape mobile orientation */
  1131→@media (max-height: 500px) and (orientation: landscape) {
  1132→  .landscape-optimize {
  1133→    height: 100vh;
  1134→    overflow-y: auto;
  1135→  }
  1136→  
  1137→  .notifications-container {
  1138→    top: var(--spacing-2);
  1139→    right: var(--spacing-2);
  1140→  }
  1141→  
  1142→  .chat-widget-container {
  1143→    bottom: var(--spacing-2);
  1144→    right: var(--spacing-2);
  1145→  }
  1146→}
  1147→
  1148→/* High DPI displays (retina) */
  1149→@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1150→  .retina-optimize {
  1151→    image-rendering: -webkit-optimize-contrast;
  1152→    image-rendering: crisp-edges;
  1153→  }
  1154→  
  1155→  .retina-optimize img {
  1156→    image-rendering: -webkit-optimize-contrast;
  1157→    image-rendering: crisp-edges;
  1158→  }
  1159→}
  1160→
  1161→/* Hover detection for touch devices */
  1162→@media (hover: none) {
  1163→  .hover-only {
  1164→    display: none !important;
  1165→  }
  1166→  
  1167→  .btn-base:hover {
  1168→    transform: none !important;
  1169→  }
  1170→  
  1171→  .card-base:hover {
  1172→    transform: none !important;
  1173→    box-shadow: var(--shadow-sm) !important;
  1174→  }
  1175→}
  1176→
  1177→/* Pointer device optimization */
  1178→@media (pointer: coarse) {
  1179→  /* Touch devices */
  1180→  .form-control-base {
  1181→    font-size: 16px; /* Prevents zoom on iOS */
  1182→    padding: var(--spacing-3);
  1183→  }
  1184→  
  1185→  .btn-base {
  1186→    padding: var(--spacing-3) var(--spacing-4);
  1187→  }
  1188→  
  1189→  select,
  1190→  input,
  1191→  textarea {
  1192→    font-size: 16px; /* Prevents zoom on iOS */
  1193→  }
  1194→}
  1195→
  1196→/* Safe area insets for devices with notches */
  1197→@supports (padding: max(0px)) {
  1198→  .safe-area-inset-top {
  1199→    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1200→  }
  1201→  
  1202→  .safe-area-inset-bottom {
  1203→    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1204→  }
  1205→  
  1206→  .safe-area-inset-left {
  1207→    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1208→  }
  1209→  
  1210→  .safe-area-inset-right {
  1211→    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1212→  }
  1213→}
  1214→
  1215→/* PWA display modes */
  1216→@media (display-mode: standalone) {
  1217→  .pwa-standalone {
  1218→    padding-top: var(--spacing-4);
  1219→  }
  1220→  
  1221→  .pwa-standalone .navbar {
  1222→    padding-top: env(safe-area-inset-top);
  1223→  }
  1224→}
  1225→
  1226→/* Connection-aware styles */
  1227→.slow-connection .glass-medium,
  1228→.slow-connection .glass-strong {
  1229→  backdrop-filter: none !important;
  1230→  -webkit-backdrop-filter: none !important;
  1231→  background: var(--color-surface-primary) !important;
  1232→}
  1233→
  1234→.slow-connection img {
  1235→  filter: blur(2px);
  1236→  transition: filter var(--transition-base);
  1237→}
  1238→
  1239→.slow-connection img.loaded {
  1240→  filter: none;
  1241→}
  1242→
  1243→/* Gesture feedback */
  1244→.gesture-feedback {
  1245→  position: relative;
  1246→  overflow: hidden;
  1247→}
  1248→
  1249→.gesture-feedback::after {
  1250→  content: '';
  1251→  position: absolute;
  1252→  top: 50%;
  1253→  left: 50%;
  1254→  width: 0;
  1255→  height: 0;
  1256→  border-radius: 50%;
  1257→  background: rgba(0, 0, 0, 0.1);
  1258→  transform: translate(-50%, -50%);
  1259→  transition: width 0.3s ease, height 0.3s ease;
  1260→  pointer-events: none;
  1261→}
  1262→
  1263→.gesture-feedback.active::after {
  1264→  width: 100px;
  1265→  height: 100px;
  1266→}
  1267→
  1268→/* Dark mode gesture feedback */
  1269→[data-theme="dark"] .gesture-feedback::after {
  1270→  background: rgba(255, 255, 255, 0.1);
  1271→}
  1272→
  1273→/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1274→
  1275→/* Swipe gesture indicators */
  1276→.swipe-indicator {
  1277→  position: relative;
  1278→}
  1279→
  1280→.swipe-indicator::before {
  1281→  content: '';
  1282→  position: absolute;
  1283→  top: 50%;
  1284→  right: var(--spacing-4);
  1285→  width: 20px;
  1286→  height: 2px;
  1287→  background: var(--color-gray-400);
  1288→  transform: translateY(-50%);
  1289→  opacity: 0.5;
  1290→}
  1291→
  1292→.swipe-indicator::after {
  1293→  content: '';
  1294→  position: absolute;
  1295→  top: 50%;
  1296→  right: var(--spacing-3);
  1297→  width: 0;
  1298→  height: 0;
  1299→  border-left: 6px solid var(--color-gray-400);
  1300→  border-top: 4px solid transparent;
  1301→  border-bottom: 4px solid transparent;
  1302→  transform: translateY(-50%);
  1303→  opacity: 0.5;
  1304→}
  1305→
  1306→/* Loading states optimized for mobile */
  1307→.mobile-loading {
  1308→  display: flex;
  1309→  flex-direction: column;
  1310→  align-items: center;
  1311→  justify-content: center;
  1312→  min-height: 200px;
  1313→  padding: var(--spacing-8);
  1314→}
  1315→
  1316→.mobile-loading .loading-spinner {
  1317→  width: 40px;
  1318→  height: 40px;
  1319→  border: 3px solid var(--color-gray-200);
  1320→  border-top: 3px solid var(--color-primary-500);
  1321→  border-radius: 50%;
  1322→  animation: spin 1s linear infinite;
  1323→}
  1324→
  1325→.mobile-loading .loading-text {
  1326→  margin-top: var(--spacing-4);
  1327→  font-size: var(--font-size-sm);
  1328→  color: var(--color-text-secondary);
  1329→  text-align: center;
  1330→}
  1331→
  1332→/* Battery optimization styles */
  1333→.battery-optimize {
  1334→  animation-play-state: paused !important;
  1335→}
  1336→
  1337→.battery-optimize * {
  1338→  animation-play-state: paused !important;
  1339→  transition-duration: 0.1s !important;
  1340→}
  1341→
  1342→.battery-optimize .glass-medium,
  1343→.battery-optimize .glass-strong {
  1344→  backdrop-filter: none !important;
  1345→  -webkit-backdrop-filter: none !important;
  1346→  background: var(--color-surface-primary) !important;
  1347→}
  1348→
  1349→.battery-optimize .animate-pulse,
  1350→.battery-optimize .animate-bounce,
  1351→.battery-optimize .animate-spin {
  1352→  animation: none !important;
  1353→}
  1354→
  1355→/* Accessibility enhancements for mobile */
  1356→@media (max-width: 768px) {
  1357→  .mobile-a11y button,
  1358→  .mobile-a11y [role="button"],
  1359→  .mobile-a11y input,
  1360→  .mobile-a11y select {
  1361→    min-height: 44px;
  1362→    font-size: 16px;
  1363→  }
  1364→  
  1365→  .mobile-a11y .focus-ring:focus-visible {
  1366→    outline: 3px solid var(--color-primary-500);
  1367→    outline-offset: 2px;
  1368→  }
  1369→}

