        :is="headingLevel"
        id="cta-heading"
        :class="headingClasses"
        :style="headingStyle"
        v-html="heading"
      />

      <!-- Description with proper semantics -->
      <p
        id="cta-description"
        :class="descriptionClasses"
        :style="descriptionStyle"
        v-html="description"
      />

      <!-- CTA with full keyboard support -->
      <component
        :is="ctaType === 'link' ? 'a' : 'button'"
        ref="ctaRef"
        :class="ctaClasses"
        :style="ctaStyle"
        v-bind="linkAttrs"
        :type="ctaType === 'button' ? 'button' : undefined"
        :aria-label="`${ctaText}. Opens in ${isExternalLink ? 'new window' : 'same window'}.`"
        tabindex="0"
        @click="handleCtaClick"
        @keydown="handleKeydown"
        @focus="handleFocus"
        @blur="handleBlur"
        @mouseenter="handleMouseEnter"
        @mouseleave="handleMouseLeave"
      >
        {{ ctaText }}
      </component>

      <!-- Screen reader announcement region -->
      <div
        class="sr-only"
        role="status"
        aria-live="polite"
        aria-atomic="true"
      >
        Call to action button available. Press Enter or Space to activate.
      </div>
    </div>
  </section>
</template>

<style>
/* Respect user motion preferences */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Screen reader only utility */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

/* Enhanced focus visible for keyboard navigation */
:focus-visible {
  outline: 3px solid white;
  outline-offset: 4px;
}

/* Remove focus for mouse users */
:focus:not(:focus-visible) {
  outline: none;
}
</style>
