    79→  --color-gray-200: #e5e7eb;
    80→  --color-gray-300: #d1d5db;
    81→  --color-gray-400: #9ca3af;
    82→  --color-gray-500: #6b7280;
    83→  --color-gray-600: #4b5563;
    84→  --color-gray-700: #374151;
    85→  --color-gray-800: #1f2937;
    86→  --color-gray-900: #111827;
    87→  --color-gray-950: #030712;
    88→
    89→  /* Surface Colors */
    90→  --color-surface-primary: #ffffff;
    91→  --color-surface-secondary: var(--color-gray-50);
    92→  --color-surface-tertiary: var(--color-gray-100);
    93→  --color-surface-inverse: var(--color-gray-900);
    94→
    95→  /* Border Colors */
    96→  --color-border-primary: var(--color-gray-200);
    97→  --color-border-secondary: var(--color-gray-300);
    98→  --color-border-focus: var(--color-primary-500);
    99→  --color-border-error: var(--color-error-500);
   100→  --color-border-success: var(--color-success-500);
   101→
   102→  /* Text Colors */
   103→  --color-text-primary: var(--color-gray-900);
   104→  --color-text-secondary: var(--color-gray-600);
   105→  --color-text-tertiary: var(--color-gray-500);
   106→  --color-text-inverse: #ffffff;
   107→  --color-text-placeholder: var(--color-gray-400);
   108→
   109→  /* ===== DARK MODE COLORS ===== */
   110→  --color-dark-surface-primary: var(--color-gray-800);
   111→  --color-dark-surface-secondary: var(--color-gray-900);
   112→  --color-dark-surface-tertiary: var(--color-gray-950);
   113→  --color-dark-surface-inverse: #ffffff;
   114→
   115→  --color-dark-border-primary: var(--color-gray-700);
   116→  --color-dark-border-secondary: var(--color-gray-600);
   117→  --color-dark-border-focus: var(--color-primary-400);
   118→
   119→  --color-dark-text-primary: var(--color-gray-100);
   120→  --color-dark-text-secondary: var(--color-gray-300);
   121→  --color-dark-text-tertiary: var(--color-gray-400);
   122→  --color-dark-text-placeholder: var(--color-gray-500);
   123→
   124→  /* ===== SPACING SYSTEM ===== */
   125→  --spacing-0: 0;
   126→  --spacing-px: 1px;
   127→  --spacing-0_5: 0.125rem;  /* 2px */
   128→  --spacing-1: 0.25rem;     /* 4px */
   129→  --spacing-1_5: 0.375rem;  /* 6px */
   130→  --spacing-2: 0.5rem;      /* 8px */
   131→  --spacing-2_5: 0.625rem;  /* 10px */
   132→  --spacing-3: 0.75rem;     /* 12px */
   133→  --spacing-3_5: 0.875rem;  /* 14px */
   134→  --spacing-4: 1rem;        /* 16px */
   135→  --spacing-5: 1.25rem;     /* 20px */
   136→  --spacing-6: 1.5rem;      /* 24px */
   137→  --spacing-7: 1.75rem;     /* 28px */
   138→  --spacing-8: 2rem;        /* 32px */
   139→  --spacing-9: 2.25rem;     /* 36px */
   140→  --spacing-10: 2.5rem;     /* 40px */
   141→  --spacing-11: 2.75rem;    /* 44px */
   142→  --spacing-12: 3rem;       /* 48px */
   143→  --spacing-14: 3.5rem;     /* 56px */
   144→  --spacing-16: 4rem;       /* 64px */
   145→  --spacing-20: 5rem;       /* 80px */
   146→  --spacing-24: 6rem;       /* 96px */
   147→  --spacing-28: 7rem;       /* 112px */
   148→  --spacing-32: 8rem;       /* 128px */
   149→
   150→  /* ===== BORDER RADIUS ===== */
   151→  --radius-none: 0;
   152→  --radius-sm: 0.125rem;    /* 2px */
   153→  --radius-base: 0.25rem;   /* 4px */
   154→  --radius-md: 0.375rem;    /* 6px */
   155→  --radius-lg: 0.5rem;      /* 8px */
   156→  --radius-xl: 0.75rem;     /* 12px */
   157→  --radius-2xl: 1rem;       /* 16px */
   158→  --radius-3xl: 1.5rem;     /* 24px */
   159→  --radius-full: 9999px;
   160→
   161→  /* ===== SHADOWS ===== */
   162→  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
   163→  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
   164→  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
   165→  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
   166→  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
   167→  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
   168→  --shadow-inner: inset 0 2px 4px 0 rgb(0 0 0 / 0.05);
   169→
   170→  /* Focus Shadows */
   171→  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.1);
   172→  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.1);
   173→  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.1);
   174→
   175→  /* ===== TRANSITIONS ===== */
   176→  --transition-fast: 150ms ease;
   177→  --transition-base: 200ms ease;
   178→  --transition-medium: 300ms ease;
   179→  --transition-slow: 500ms ease;
   180→
   181→  /* Easing Functions */
   182→  --ease-linear: linear;
   183→  --ease-in: cubic-bezier(0.4, 0, 1, 1);
   184→  --ease-out: cubic-bezier(0, 0, 0.2, 1);
   185→  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
   186→  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   187→
   188→  /* ===== BACKGROUND GRADIENTS ===== */
   189→
   190→  /* Hero/Landing Gradients - Matching original multi-radial design */
   191→  --gradient-hero-base: linear-gradient(to bottom, #ffffff, #f8fafc);
   192→  --gradient-hero-radial-1: radial-gradient(1100px 1100px at 8% 8%, rgba(99, 102, 241, 0.12), transparent 60%);
   193→  --gradient-hero-radial-2: radial-gradient(1200px 1200px at 92% 92%, rgba(168, 85, 247, 0.11), transparent 60%);
   194→  --gradient-hero-radial-3: radial-gradient(1300px 900px at -10% 50%, rgba(45, 212, 191, 0.08), transparent 65%);
   195→  --gradient-hero-radial-4: radial-gradient(1300px 900px at 110% 45%, rgba(59, 130, 246, 0.08), transparent 65%);
   196→  --gradient-hero-radial-5: radial-gradient(900px 900px at 95% 10%, rgba(236, 72, 153, 0.06), transparent 65%);
   197→
   198→  /* Alternative simpler gradients */
   199→  --gradient-hero-vibrant: linear-gradient(135deg, var(--color-primary-100) 0%, var(--color-primary-200) 100%);
   200→  --gradient-hero-subtle: linear-gradient(180deg, var(--color-gray-50) 0%, var(--color-surface-primary) 100%);
   201→
   202→  /* Accent Gradients */
   203→  --gradient-accent-primary: linear-gradient(135deg, var(--color-primary-400) 0%, var(--color-primary-600) 100%);
   204→  --gradient-accent-purple: linear-gradient(135deg, #c084fc 0%, #7c3aed 100%);
   205→  --gradient-accent-warm: linear-gradient(135deg, #fb923c 0%, #f59e0b 100%);
   206→
   207→  /* Feature Section Gradients */
   208→  --gradient-feature-light: linear-gradient(to right, var(--color-primary-50), var(--color-gray-50));
   209→  --gradient-feature-card: linear-gradient(to bottom, var(--color-surface-primary), var(--color-surface-secondary));
   210→
   211→  /* CTA/Interactive Gradients */
   212→  --gradient-cta: linear-gradient(135deg, var(--color-primary-500) 0%, var(--color-primary-600) 100%);
   213→  --gradient-cta-hover: linear-gradient(135deg, var(--color-primary-600) 0%, var(--color-primary-700) 100%);
   214→
   215→  /* ===== REPURPOSE.IO BRAND COLORS ===== */
   216→
   217→  /* Repurpose.io Pink Gradient (Primary CTA) */
   218→  --color-repurpose-pink-start: #FF6B9D;
   219→  --color-repurpose-pink-end: #FE88B1;
   220→
   221→  /* Repurpose.io Purple (Navigation Hover) */
   222→  --color-repurpose-purple: #7B68EE;
   223→  --color-repurpose-purple-hover: #6B5DD3;
   224→
   225→  /* Repurpose.io Blue (Subheadings & Stats) */
   226→  --color-repurpose-blue: #6B7FFF;
   227→
   228→  /* Repurpose.io Text Colors */
   229→  --color-repurpose-text-primary: #2D2D2D;
   230→  --color-repurpose-text-secondary: #1A1A1A;
   231→  --color-repurpose-text-tertiary: #6B6B6B;
   232→
   233→  /* Repurpose.io Border Colors */
   234→  --color-repurpose-border-light: #F0F0F0;
   235→  --color-repurpose-border-medium: #E8E8E8;
   236→  --color-repurpose-border-dark: #D0D0D0;
   237→
   238→  /* Repurpose.io Background Colors */
   239→  --color-repurpose-bg-light: #FAFAFA;
   240→  --color-repurpose-bg-white: #FFFFFF;
   241→  --color-repurpose-bg-gray-light: #F8F8F8;
   242→
   243→  /* Repurpose.io Gradients */
   244→  --gradient-repurpose-cta: linear-gradient(135deg, var(--color-repurpose-pink-start) 0%, var(--color-repurpose-pink-end) 100%);
   245→  --gradient-repurpose-cta-hover: linear-gradient(135deg, #FF5A8C 0%, #FD77A0 100%);
   246→  --gradient-repurpose-purple: linear-gradient(90deg, #7B68EE 0%, #9370DB 100%);
   247→  --gradient-repurpose-card-blue: linear-gradient(135deg, #F8F9FF 0%, #FFFFFF 100%);
   248→  --gradient-repurpose-card-pink: linear-gradient(135deg, #FFF8FA 0%, #FFFFFF 100%);
   249→
   250→  /* Repurpose.io Shadows */
   251→  --shadow-repurpose-pink-md: 0 4px 12px rgba(255, 107, 157, 0.3);
   252→  --shadow-repurpose-pink-lg: 0 6px 16px rgba(255, 107, 157, 0.4);
   253→  --shadow-repurpose-pink-xl: 0 8px 24px rgba(255, 107, 157, 0.35);
   254→  --shadow-repurpose-pink-2xl: 0 12px 32px rgba(255, 107, 157, 0.45);
   255→
   256→  /* ===== REPURPOSE.IO PHONE MOCKUP TOKENS ===== */
   257→
   258→  /* Phone Dimensions */
   259→  --phone-width: 17.5rem;           /* 280px */
   260→  --phone-height: 35rem;            /* 560px */
   261→  --phone-border-radius: 2.25rem;   /* 36px */
   262→  --phone-border-width: 0.5rem;     /* 8px */
   263→  --phone-screen-radius: 1.75rem;   /* 28px (36px - 8px) */
   264→
   265→  /* Platform Icon */
   266→  --phone-icon-size: 3.5rem;        /* 56px diameter */
   267→  --phone-icon-offset: 1.25rem;     /* 20px from top/left */
   268→
   269→  /* Layout & Spacing */
   270→  --phone-gap: 2.5rem;              /* 40px gap between phones */
   271→
   272→  /* Phone Frame Colors */
   273→  --phone-frame-bg: #1A1A1A;
   274→  --phone-frame-border: #2D2D2D;
   275→  --phone-screen-bg: #FFFFFF;
   276→
   277→  /* Phone Shadows */
   278→  --shadow-phone: 0 20px 60px rgba(0, 0, 0, 0.25);
   279→  --shadow-phone-icon: 0 4px 12px rgba(0, 0, 0, 0.15);
   280→
   281→  /* Optional Mesh Gradient Overlay for Extra Depth */
   282→  --gradient-mesh-overlay:
   283→    radial-gradient(at 20% 30%, rgba(124, 58, 237, 0.08) 0px, transparent 50%),
   284→    radial-gradient(at 80% 70%, rgba(59, 130, 246, 0.08) 0px, transparent 50%),
   285→    radial-gradient(at 40% 80%, rgba(236, 72, 153, 0.06) 0px, transparent 50%);
   286→
   287→  /* ===== TYPOGRAPHY ===== */
   288→  --font-family-sans: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
   289→  --font-family-mono: 'JetBrains Mono', 'SF Mono', Monaco, Inconsolata, 'Roboto Mono', 'Source Code Pro', monospace;
   290→  --font-family-emoji: 'Noto Color Emoji', 'Apple Color Emoji', 'Segoe UI Emoji', sans-serif;
   291→
   292→  /* Font Weights */
   293→  --font-weight-thin: 100;
   294→  --font-weight-extralight: 200;
   295→  --font-weight-light: 300;
   296→  --font-weight-normal: 400;
   297→  --font-weight-medium: 500;
   298→  --font-weight-semibold: 600;
   299→  --font-weight-bold: 700;
   300→  --font-weight-extrabold: 800;
   301→  --font-weight-black: 900;
   302→
   303→  /* Font Sizes */
   304→  --font-size-xs: 0.75rem;      /* 12px */
   305→  --font-size-sm: 0.875rem;     /* 14px */
   306→  --font-size-base: 1rem;       /* 16px */
   307→  --font-size-lg: 1.125rem;     /* 18px */
   308→  --font-size-xl: 1.25rem;      /* 20px */
   309→  --font-size-2xl: 1.5rem;      /* 24px */
   310→  --font-size-3xl: 1.875rem;    /* 30px */
   311→  --font-size-4xl: 2.25rem;     /* 36px */
   312→  --font-size-5xl: 3rem;        /* 48px */
   313→  --font-size-6xl: 3.75rem;     /* 60px */
   314→  --font-size-7xl: 4.5rem;      /* 72px */
   315→  --font-size-8xl: 6rem;        /* 96px */
   316→  --font-size-9xl: 8rem;        /* 128px */
   317→
   318→  /* Line Heights */
   319→  --line-height-none: 1;
   320→  --line-height-tight: 1.25;
   321→  --line-height-snug: 1.375;
   322→  --line-height-normal: 1.5;
   323→  --line-height-relaxed: 1.625;
   324→  --line-height-loose: 2;
   325→
   326→  /* ===== COMPONENT SIZES ===== */
   327→  --size-xs: 1.5rem;    /* 24px */
   328→  --size-sm: 2rem;      /* 32px */
   329→  --size-md: 2.5rem;    /* 40px */
   330→  --size-lg: 3rem;      /* 48px */
   331→  --size-xl: 3.5rem;    /* 56px */
   332→  --size-2xl: 4rem;     /* 64px */
   333→
   334→  /* Input Heights */
   335→  --input-height-sm: var(--size-sm);
   336→  --input-height-md: var(--size-md);
   337→  --input-height-lg: var(--size-lg);
   338→
   339→  /* ===== Z-INDEX SCALE ===== */
   340→  --z-index-behind: -1;
   341→  --z-index-base: 0;
   342→  --z-index-chat: 900;
   343→  --z-index-dropdown: 1000;
   344→  --z-index-notification: 1000;
   345→  --z-index-sticky: 1020;
   346→  --z-index-fixed: 1030;
   347→  --z-index-modal-backdrop: 1040;
   348→  --z-index-modal: 1050;
   349→  --z-index-popover: 1060;
   350→  --z-index-tooltip: 1070;
   351→  --z-index-toast: 1080;
   352→  --z-index-max: 2147483647;
   353→
   354→  /* ===== ANIMATION UTILITIES ===== */
   355→  --animation-pulse: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
   356→  --animation-bounce: bounce 1s infinite;
   357→  --animation-spin: spin 1s linear infinite;
   358→  --animation-ping: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
   359→  --animation-fade-in: fadeIn var(--transition-base);
   360→  --animation-fade-out: fadeOut var(--transition-base);
   361→  --animation-slide-in-up: slideInUp var(--transition-medium) var(--ease-out);
   362→  --animation-slide-in-down: slideInDown var(--transition-medium) var(--ease-out);
   363→  --animation-scale-in: scaleIn var(--transition-base) var(--ease-out);
   364→  --animation-scale-out: scaleOut var(--transition-base) var(--ease-in);
   365→}
   366→
   367→/* ===== DARK MODE OVERRIDES ===== */
   368→[data-theme="dark"] {
   369→  --color-surface-primary: var(--color-dark-surface-primary);
   370→  --color-surface-secondary: var(--color-dark-surface-secondary);
   371→  --color-surface-tertiary: var(--color-dark-surface-tertiary);
   372→  --color-surface-inverse: var(--color-dark-surface-inverse);
   373→
   374→  --color-border-primary: var(--color-dark-border-primary);
   375→  --color-border-secondary: var(--color-dark-border-secondary);
   376→  --color-border-focus: var(--color-dark-border-focus);
   377→
   378→  --color-text-primary: var(--color-dark-text-primary);
   379→  --color-text-secondary: var(--color-dark-text-secondary);
   380→  --color-text-tertiary: var(--color-dark-text-tertiary);
   381→  --color-text-placeholder: var(--color-dark-text-placeholder);
   382→
   383→  /* Enhanced shadows for dark mode */
   384→  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.2);
   385→  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.3), 0 1px 2px -1px rgb(0 0 0 / 0.3);
   386→  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.4), 0 2px 4px -2px rgb(0 0 0 / 0.4);
   387→  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.5), 0 4px 6px -4px rgb(0 0 0 / 0.5);
   388→  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.6), 0 8px 10px -6px rgb(0 0 0 / 0.6);
   389→  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.7);
   390→
   391→  /* Dark mode focus shadows */
   392→  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.2);
   393→  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.2);
   394→  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.2);
   395→
   396→  /* Dark Mode Gradient Overrides - Matching original dark theme */
