   423→/* ===== KEYFRAME ANIMATIONS ===== */
   424→@keyframes fadeIn {
   425→  from {
   426→    opacity: 0;
   427→  }
   428→  to {
   429→    opacity: 1;
   430→  }
   431→}
   432→
   433→@keyframes fadeOut {
   434→  from {
   435→    opacity: 1;
   436→  }
   437→  to {
   438→    opacity: 0;
   439→  }
   440→}
   441→
   442→@keyframes slideInUp {
   443→  from {
   444→    opacity: 0;
   445→    transform: translateY(1rem);
   446→  }
   447→  to {
   448→    opacity: 1;
   449→    transform: translateY(0);
   450→  }
   451→}
   452→
   453→@keyframes slideInDown {
   454→  from {
   455→    opacity: 0;
   456→    transform: translateY(-1rem);
   457→  }
   458→  to {
   459→    opacity: 1;
   460→    transform: translateY(0);
   461→  }
   462→}
   463→
   464→@keyframes scaleIn {
   465→  from {
   466→    opacity: 0;
   467→    transform: scale(0.9);
   468→  }
   469→  to {
   470→    opacity: 1;
   471→    transform: scale(1);
   472→  }
   473→}
   474→
   475→@keyframes scaleOut {
   476→  from {
   477→    opacity: 1;
   478→    transform: scale(1);
   479→  }
   480→  to {
   481→    opacity: 0;
   482→    transform: scale(0.9);
   483→  }
   484→}
   485→
   486→@keyframes pulse {
   487→  0%, 100% {
   488→    opacity: 1;
   489→  }
   490→  50% {
   491→    opacity: 0.5;
   492→  }
   493→}
   494→
   495→@keyframes bounce {
   496→  0%, 100% {
   497→    transform: translateY(-25%);
   498→    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
   499→  }
   500→  50% {
   501→    transform: none;
   502→    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
   503→  }
   504→}
   505→
   506→@keyframes spin {
   507→  to {
   508→    transform: rotate(360deg);
   509→  }
   510→}
   511→
   512→@keyframes ping {
   513→  75%, 100% {
   514→    transform: scale(2);
   515→    opacity: 0;
   516→  }
   517→}
